// Cube with rounded edges (filleted)
// Overall 100 mm cube with 5 mm edge radius

$fn = 32;

edge_radius = 5;

// Main body
difference() {
    // Outer rounded cube
    minkowski() {
        cube([100 - 2*edge_radius, 100 - 2*edge_radius, 100 - 2*edge_radius], center = true);
        sphere(r = edge_radius);
    }

    // Hollow center
    minkowski() {
        cube([80 - 2*edge_radius, 80 - 2*edge_radius, 80 - 2*edge_radius], center = true);
        sphere(r = edge_radius);
    }
}

difference() {
    // Main cube body with rounded edges
    minkowski() {
        cube([60, 60, 60], center=true);
        sphere(r=5);
    }

    // Remove internal material to create rounded edges
    translate([0, 0, 0])
    minkowski() {
        cube([50, 50, 50], center=true);
        sphere(r=5);
    }

    // Top face chamfer
    translate([0, 0, 35])
    cylinder(h=30, r1=35, r2=40, center=true);

    // Bottom face chamfer
    translate([0, 0, -35])
    cylinder(h=30, r1=40, r2=35, center=true);
}

// Rounded-edge cube with chamfered top edges
size = 80;
radius = 8;
chamfer = 5;

// Main rounded cube
minkowski() {
    cube([size - 2*radius, size - 2*radius, size - 2*radius], center = true);
    sphere(r = radius, $fn = 32);
}

// Chamfer top edges
difference() {
    translate([0, 0, size/2 - chamfer]) {
        rotate([0, 0, 45]) {
            cube([size*sqrt(2), size*sqrt(2), chamfer*sqrt(2)], center = true);
        }
    }

    translate([0, 0, size/2]) {
        cube([size + 2, size + 2, chamfer*2], center = true);
    }
}

// Medium-complexity cube with rounded edges
$fn = 32;

module rounded_cube(size, radius) {
    hull() {
        for (dx = [0, size.x - 2*radius])
            for (dy = [0, size.y - 2*radius])
                for (dz = [0, size.z - 2*radius])
                    translate([dx + radius, dy + radius, dz + radius])
                        sphere(r = radius);
    }
}

rounded_cube([100, 100, 100], 8);

// Tall cube with rounded vertical edges
$fn = 32;

width = 60;
depth = 60;
height = 120;
radius = 8;

difference() {
    // Main tall block
    cube([width, depth, height], center = true);

    // Cut vertical edge rounds
    translate([0, 0, 0]) {
        // Front-left edge
        rotate([0, 90, 0])
            translate([0, -depth/2, -height/2])
                cylinder(h = width, r = radius);

        // Front-right edge
        rotate([0, 90, 0])
            translate([0, -depth/2, height/2])
                cylinder(h = width, r = radius);

        // Back-left edge
        rotate([0, 90, 0])
            translate([0, depth/2, -height/2])
                cylinder(h = width, r = radius);

        // Back-right edge
        rotate([0, 90, 0])
            translate([0, depth/2, height/2])
                cylinder(h = width, r = radius);
    }
}

$fn = 32;

// Cube with rounded edges and slightly wider body
module cube_wide() {
    width = 120;
    depth = 80;
    height = 60;
    radius = 8;

    difference() {
        // Main rounded cube
        minkowski() {
            cube([width-2*radius, depth-2*radius, height-2*radius], center = true);
            sphere(r = radius);
        }

        // Optional slight rounding on top edges only
        translate([0, 0, height/2 - radius])
            cube([width-2*radius, depth-2*radius, radius*2 + 1], center = true);
    }
}

cube_wide();

// Thin cube with rounded edges
