// hollow cube with rounded edges and internal ribs
wall = 2;
outer = 80;
inner = outer - 2*wall;
round = 5;
rib_w = 2;
rib_h = 4;

difference() {
    // outer rounded cube
    minkowski() {
        cube([outer, outer, outer], center = true);
        sphere(r = round);
    }

    // hollow center
    minkowski() {
        cube([inner, inner, inner], center = true);
        sphere(r = round - wall);
    }

    // internal support ribs along X
    translate([0, 0, 0])
        difference() {
            cube([outer - 2*round, rib_w, rib_h], center = true);
            cube([inner, rib_w + 0.1, rib_h + 0.1], center = true);
        }

    // internal support ribs along Y
    translate([0, 0, 0])
        difference() {
            cube([rib_w, outer - 2*round, rib_h], center = true);
            cube([rib_w + 0.1, inner, rib_h + 0.1], center = true);
        }
}

// cube_rounded - 80 mm cube with 8 mm radius rounded edges
$fn = 32;

module rounded_edge() {
    cylinder(h=80, r=8);
}

module rounded_corner() {
    sphere(r=8);
}

difference() {
    cube([80, 80, 80], center=true);

    // Carve away sharp edges
    for(x=[-36, 36])
        for(y=[-36, 36])
            translate([x, y, 0])
                rotate([0, 0, 45])
                    rounded_edge();

    // Carve away sharp corners
    for(x=[-36, 36])
        for(y=[-36, 36])
            for(z=[-36, 36])
                translate([x, y, z])
                    rounded_corner();
}

// chamfered cube 100 mm on edge with 5 mm 45° chamfers on all edges

ch = 5;          // chamfer length
s  = 100;        // cube size

difference() {
    cube([s, s, s], center = true);

    // top edges
    for (a = [0, 90, 180, 270])
        rotate([0, 0, a])
            translate([s/2 - ch, 0, s/2 - ch])
                rotate([0, 45, 0])
                    cube([ch*sqrt(2), ch*2, ch*sqrt(2)], center = true);

    // bottom edges
    for (a = [0, 90, 180, 270])
        rotate([0, 0, a])
            translate([s/2 - ch, 0, -s/2 + ch])
                rotate([0, -45, 0])
                    cube([ch*sqrt(2), ch*2, ch*sqrt(2)], center = true);

    // vertical edges
    for (a = [0, 90, 180, 270])
        rotate([0, 0, a])
            translate([s/2 - ch, s/2 - ch, 0])
                rotate([45, 0, 0])
                    cube([ch*sqrt(2), ch*sqrt(2), ch*2], center = true);
}

