// Medium-complexity sphere with rounded equatorial groove
// Overall Ø 100 mm

module grooved_sphere(r=50, groove_r=3, groove_depth=2) {
    difference() {
        sphere(r=r, $fn=100);               // main sphere
        rotate([0,90,0])                    // rotate groove to equator
            difference() {
                cylinder(h=r*2.2, r=r+1, center=true, $fn=100);  // cutting band
                cylinder(h=r*2.3, r=r-groove_depth, center=true, $fn=100);
            }
    }
}

grooved_sphere();

