// slice along X, Y, and Z axes to keep only the positive octant
difference() {
    sphere(r);

    // remove negative X
    translate([-r-1, 0, 0])
        cube([r+1, 2*r+2, 2*r+2], center=true);

    // remove negative Y
    translate([0, -r-1, 0])
        cube([2*r+2, r+1, 2*r+2], center=true);

