// sphere_oblong: stretched sphere with rounded ends
module sphere_oblong(length=100, radius=25){
    // main body cylinder
    cylinder(h=length-2*radius, r=radius, center=true);

    // end cap spheres
    translate([0,0,(length/2)-radius]) sphere(r=radius);
    translate([0,0,-(length/2)+radius]) sphere(r=radius);
}

