// Cylinder with rounded top and bottom edges
h = 80;
r = 25;
fillet = 4;

difference() {
    // Main cylinder plus edge spheres
    union() {
        cylinder(h = h, r = r, center = true);
        translate([0, 0,  h/2]) sphere(r = fillet);
        translate([0, 0, -h/2]) sphere(r = fillet);
    }

    // Cut away excess sphere material
    translate([0, 0,  h/2 + fillet]) cube([r*2+1, r*2+1, fillet*2], center = true);
    translate([0, 0, -h/2 - fillet]) cube([r*2+1, r*2+1, fillet*2], center = true);
}

// Short cylinder with rounded top and bottom edges
$fn = 50;                      // Smooth curves

h = 60;                        // Total height
r = 30;                        // Main radius
edge = 5;                      // Rounding radius

// Main cylinder body minus top/bottom caps to create rounding
difference() {
    cylinder(h = h, r = r);

    // Remove top sharp edge
    translate([0, 0, h - edge])
        cylinder(h = edge + 1, r1 = r, r2 = r - edge);

    // Remove bottom sharp edge
    translate([0, 0, -1])
        cylinder(h = edge + 1, r1 = r - edge, r2 = r);
}

// Add rounded top cap
translate([0, 0, h - edge])
    sphere(r = edge);

// Add rounded bottom cap
translate([0, 0, edge])
    sphere(r = edge);

// Tall cylinder with rounded top and bottom edges
module cylinder_tall() {
    height = 100;
    radius = 30;
    fillet = 5;

    difference() {
        // Main cylinder
        cylinder(h=height, r=radius, $fn=64);

        // Top edge rounding
        translate([0, 0, height - fillet])
            cylinder(h=fillet, r1=radius, r2=radius - fillet, $fn=64);

        // Bottom edge rounding
        translate([0, 0, fillet])
            cylinder(h=fillet, r1=radius - fillet, r2=radius, $fn=64);
    }

    // Top rounded cap
    translate([0, 0, height - fillet])
        sphere(r=fillet, $fn=64);

    // Bottom rounded base
    translate([0, 0, fillet])
        sphere(r=fillet, $fn=64);
}

cylinder_tall();

// Thin cylinder with rounded top and bottom edges
$fn = 64;

module cylinder_thin(height = 80, radius = 25, thickness = 3, fillet = 2) {
    difference() {
        // Outer cylinder
        cylinder(h = height, r = radius);

        // Inner hollow
        translate([0, 0, -1])
            cylinder(h = height + 2, r = radius - thickness);

        // Top fillet removal
        translate([0, 0, height - fillet])
            rotate_extrude(angle = 90)
                translate([radius - thickness, 0, 0])
                    circle(r = fillet);

        // Bottom fillet removal
        translate([0, 0, fillet])
            rotate_extrude(angle = 90)
                translate([radius - thickness, 0, 0])
                    circle(r = fillet);
    }

    // Add fillet material
    union() {
        // Top fillet
        translate([0, 0, height - fillet])
            rotate_extrude(angle = 90)
                translate([radius - thickness - fillet, 0, 0])
                    circle(r = fillet);

        // Bottom fillet
        translate([0, 0, fillet])
            rotate_extrude(angle = 270)
                translate([radius - thickness - fillet, 0, 0])
                    circle(r = fillet);
    }
}

cylinder_thin();

// Cylinder with rounded edges and thickened base
height = 80;
radius = 30;
base_thickness = 15;
edge_radius = 5;

difference() {
    // Main cylinder with rounded edges
    union() {
        // Cylinder body
        cylinder(h=height, r=radius, $fn=64);

        // Rounded top edge
        translate([0, 0, height])
            minkowski() {
                cylinder(h=0.1, r=radius-edge_radius, $fn=64);
                sphere(r=edge_radius, $fn=32);
            }

        // Thickened base
        cylinder(h=base_thickness, r=radius+10, $fn=64);
    }

    // Hollow center
    cylinder(h=height+base_thickness, r=radius-5, $fn=64);
}

