outer_r = 30;
inner_r = 20;
height = 80;
fillet_r = 2;

difference() {
    // Outer cylinder with rounded edges
    union() {
        // Main outer cylinder
        cylinder(h=height - 2*fillet_r, r=outer_r, center=true);

        // Top rounded edge
        translate([0, 0, height/2 - fillet_r])
            cylinder(h=2*fillet_r, r1=outer_r, r2=outer_r - fillet_r, center=true);

        // Bottom rounded edge
        translate([0, 0, -height/2 + fillet_r])
            cylinder(h=2*fillet_r, r1=outer_r - fillet_r, r2=outer_r, center=true);
    }

    // Inner hollow cylinder
    cylinder(h=height + 2, r=inner_r, center=true);
}

