difference() {
    union() {
        // main cylinder body
        cylinder(h = h, r = r, $fn = 64);

        // rounded top edge
        translate([0, 0, h])
            cylinder(h = edge_r, r1 = r, r2 = r + edge_r, $fn = 64);
