// Cylinder with flat ends and rounded top/bottom edges
$fn = 50;

diameter = 60;
height = 80;
edge_radius = 4;

difference() {
    cylinder(h = height, r = diameter/2);

    // Rounded top edge
    translate([0, 0, height - edge_radius])
    rotate_extrude(angle = 90)
    translate([diameter/2 - edge_radius, 0])
    circle(r = edge_radius);

    // Rounded bottom edge
    mirror([0, 0, 1])
    translate([0, 0, height - edge_radius])
    rotate_extrude(angle = 90)
    translate([diameter/2 - edge_radius, 0])
    circle(r = edge_radius);
}

// Medium-complexity cylinder with rounded top and bottom edges
cylinder_diameter_large = 80;   // diameter 80 mm
cylinder_height      = 60;      // height 60 mm
edge_radius          = 8;       // 8 mm radius rounding

// Main body minus sharp edges
difference() {
    cylinder(h = cylinder_height, d = cylinder_diameter_large, $fn = 64);

    // Top edge chamfer
    translate([0, 0, cylinder_height - edge_radius])
        cylinder(h = edge_radius + 1, 
                 r1 = cylinder_diameter_large/2 - edge_radius, 
                 r2 = cylinder_diameter_large/2 + 1, 
                 $fn = 64);

    // Bottom edge chamfer
    translate([0, 0, -1])
        cylinder(h = edge_radius + 1, 
                 r1 = cylinder_diameter_large/2 + 1, 
                 r2 = cylinder_diameter_large/2 - edge_radius, 
