// wedge_rounded.scad – medium-complexity geometric primitive

module wedge_rounded(w=80, d=60, h=40, r=5, fn=32) {
    // outer block minus corner spheres to create rounded edges
    difference() {
        cube([w, d, h], center=false);
        for(x=[0, w]) for(y=[0, d]) for(z=[0, h])
            translate([x, y, z])
                sphere(r=r, $fn=fn);
    }

    // sloped top face
    difference() {
        translate([0, 0, h])
            cube([w, d, d], center=false);
        rotate([0, 0, atan(d/h)])
            translate([0, -1, -h])
                cube([w, d+2, h*2], center=false);
    }

    // rounding the sloped edges
    intersection() {
        children();
        difference() {
            cube([w+2*r, d+2*r, h+r], center=false);
            for(x=[-r, w+r]) for(y=[-r, d+r]) for(z=[-r, h+r])
                translate([x, y, z])
                    sphere(r=r, $fn=fn);
        }
    }
}

