    cube([block_size, block_size, block_size], center=true);

    // Remove corners to prepare for edge rounding
    for(x = [-1, 1])
    for(y = [-1, 1])
    for(z = [-1, 1])
    translate([x*(block_size/2 - edge_radius), 
               y*(block_size/2 - edge_radius), 
               z*(block_size/2 - edge_radius)])
    cube([edge_radius*2, edge_radius*2, edge_radius*2], center=true);
