// Add rounded edges
union() {
    // Main cube body
    cube([block_size-edge_radius*2, block_size-edge_radius*2, block_size-edge_radius*2], center=true);

    // Edge cylinders for rounding
    for(i = [-1, 1])
    for(j = [-1, 1]) {
        // Vertical edges
        translate([i*(block_size/2-edge_radius), j*(block_size/2-edge_radius), 0])
        cylinder(r=edge_radius, h=block_size-edge_radius*2, center=true, $fn=16);

        // Horizontal edges X
        rotate([0, 90, 0])
        translate([i*(block_size/2-edge_radius), 0, j*(block_size/2-edge_radius)])
        cylinder(r=edge_radius, h=block_size-edge_radius*2, center=true, $fn=16);

        // Horizontal edges Y
        rotate([90, 0, 0])
        translate([0, i*(block_size/2-edge_radius), j*(block_size/2-edge_radius)])
        cylinder(r=edge_radius, h=block_size-edge_radius*2, center=true, $fn=16);
    }

    // Corner spheres
    for(x = [-1, 1])
    for(y = [-1, 1])
    for(z = [-1, 1])
    translate([x*(block_size/2-edge_radius), 
               y*(block_size/2-edge_radius), 
               z*(block_size/2-edge_radius)])
    sphere(r=edge_radius, $fn=16);
}

