            for(x = [4, 76]) {
                translate([x, 4, 4])
                    rotate([0, 90, 0])
                        cylinder(r=4, h=32);
                translate([x, 36, 4])
                    rotate([0, 90, 0])
                        cylinder(r=4, h=32);
            }

            // Edge cylinders along Y
            for(y = [4, 36]) {
                translate([4, y, 4])
                    rotate([90, 0, 0])
                        cylinder(r=4, h=72);
                translate([76, y, 4])
                    rotate([90, 0, 0])
                        cylinder(r=4, h=72);
            }

            // Vertical cylinders
            for(x = [4, 76])
                for(y = [4, 36])
                    translate([x, y, 4])
                        cylinder(r=4, h=22);
        }

        // Remove corner spheres
        for(x = [0, 80])
            for(y = [0, 40])
                for(z = [0, 30])
