// cube_eighth - medium complexity
// one corner of a cube with rounded edges

module cube_eighth(size = 100, radius = 8) {
    offset = radius * (sqrt(2) - 1);

    difference() {
        cube([size, size, size]);

        // remove the other 7/8ths
        translate([0, 0, size])
            cube([size, size, size]);
        translate([0, size, 0])
            cube([size, size, size]);
        translate([size, 0, 0])
            cube([size, size, size]);

        // round the three outer edges
        translate([size - offset, size - offset, 0])
            rotate([0, 0, 45])
            cylinder(h = size + 2*radius, r = radius, center = true);

        translate([size - offset, 0, size - offset])
            rotate([0, 45, 0])
            cylinder(h = size + 2*radius, r = radius, center = true);

        translate([0, size - offset, size - offset])
            rotate([45, 0, 0])
            cylinder(h = size + 2*radius, r = radius, center = true);

        // round the corner
        translate([size - offset, size - offset, size - offset])
            sphere(r = radius);
    }
}

