// 100 mm diameter, 5 mm thick, 3 mm edge radius

module rounded_plate(diameter, thickness, edge_radius) {
    // Main plate body
    difference() {
        // Overall rounded top/bottom
        cylinder(h=thickness, r=(diameter/2) - edge_radius, $fn=100);

        // Edge rounding on top
        translate([0, 0, thickness - edge_radius])
            cylinder(h=edge_radius, r1=(diameter/2) - edge_radius - edge_radius, r2=(diameter/2) - edge_radius, $fn=100);

        // Edge rounding on bottom
        translate([0, 0, 0])
            cylinder(h=edge_radius, r1=(diameter/2) - edge_radius, r2=(diameter/2) - edge_radius - edge_radius, $fn=100);
    }

    // Outer edge rounding
    union() {
        // Top edge fillet
        translate([0, 0, thickness - edge_radius])
            rotate_extrude($fn=100)
                translate([(diameter/2) - edge_radius, 0, 0])
                    circle(r=edge_radius);

        // Bottom edge fillet
        translate([0, 0, edge_radius])
            rotate_extrude($fn=100)
                translate([(diameter/2) - edge_radius, 0, 0])
                    circle(r=edge_radius);
    }
}

