// dome_steep - medium complexity
// Steep dome (hemisphere) with rounded edge at equator

diameter = 100;
radius = diameter/2;
edge_radius = 5;

difference() {
    // Main dome
    sphere(r = radius);

    // Cut bottom half to make dome
    translate([0, 0, -radius])
        cube([diameter*2, diameter*2, radius], center = true);

    // Remove sharp edge at equator
    translate([0, 0, 0.1])
        cylinder(h = edge_radius*2, r = radius + edge_radius, center = true);

    // Add rounded edge
    translate([0, 0, 0])
        cylinder(h = edge_radius*2, r = radius - edge_radius, center = true);
}

// Rounded edge ring
translate([0, 0, 0])
    torus(r1 = radius, r2 = edge_radius);

module torus(r1, r2) {
    rotate_extrude()
        translate([r1, 0, 0])
            circle(r = r2);
}

// hollow dome with rounded rim
