difference() {
    // outside dome
    intersection() {
        sphere(outer_r);
        translate([0, 0, -outer_r + height])
            cube([outer_r*2, outer_r*2, outer_r], center = true);
    }

    // inside hollow
    intersection() {
        sphere(outer_r - wall);
        translate([0, 0, -outer_r + wall + height])
            cube([(outer_r - wall)*2, (outer_r - wall)*2, outer_r], center = true);
    }

    // round the rim
    translate([0, 0, height - rim_rad])
        minkowski() {
            cylinder(h = 1, r = outer_r - rim_rad);
            sphere(rim_rad);
        }
}

