    flute_width = 2 * PI * radius / flutes / 2;

    // Main post
    difference() {
        // Base cylinder with fillets
        cylinder(h=height, r=radius, $fn=64);

        // Flutes (vertical grooves)
        for(i=[0:flutes-1]) {
            rotate([0, 0, i * 360/flutes])
                translate([radius - flute_width/2, 0, 0])
                    cube([flute_width, radius*2, height], center=true);
