module boss_conical(h = 20, r_base = 20, r_top = 15, r_bore = 6, h_bore = 8, fillet = 2) {
    difference() {
        union() {
            // main conical body
            cylinder(h = h, r1 = r_base, r2 = r_top, $fn = 64);

            // rounded top edge
            translate([0, 0, h])
                rotate_extrude($fn = 64)
                    translate([r_top - fillet, 0])
