// Elongated capsule (sphere stretched to cylinder)
// Medium: combines sphere and cylinder with rounded ends

length = 100;
diameter = 50;
radius = diameter / 2;

module capsule(len, dia) {
    $fn = 32;
    difference() {
        union() {
            // Central cylinder
            cylinder(h = len - dia, r = dia/2, center = true);

            // End spheres
            translate([0, 0, (len - dia)/2])
                sphere(r = dia/2);

            translate([0, 0, -(len - dia)/2])
                sphere(r = dia/2);
        }

        // Optional: remove slight overlap if desired
        cube([dia, dia, len + 2], center = true);
    }
}

