// OpenSCAD Laptop Model - Realistic Style, Medium Complexity
// Overall size: ~90mm x 60mm x 12mm (closed)

// Base dimensions
laptop_x = 90;
laptop_y = 60;
laptop_z = 6;
screen_thickness = 4;

// Colors
$fn = 50;
color_base = [0.2, 0.2, 0.2];  // Dark gray
color_screen = [0.05, 0.05, 0.05];  // Black
color_keys = [0.1, 0.1, 0.1];  // Dark gray keys
color_trackpad = [0.15, 0.15, 0.15];  // Slightly lighter
color_ports = [0.3, 0.3, 0.3];  // Medium gray
color_logo = [0.5, 0.5, 0.5];  // Light gray

// Main laptop base
module laptop_base() {
    color(color_base)
    difference() {
        // Main body
        cube([laptop_x, laptop_y, laptop_z], center = true);

        // Taper edges
        translate([0, 0, laptop_z/2 - 0.5])
        cube([laptop_x - 4, laptop_y - 4, 1], center = true);
    }
}

// Keyboard keys
module keyboard() {
    key_width = 6;
    key_height = 6;
    key_depth = 0.5;
    spacing = 1.5;

    // Keyboard area
    translate([0, 10, laptop_z/2 + 0.1])
    color(color_keys)
    for (row = [0:4]) {
        for (col = [0:11]) {
            if (!(row == 4 && col > 6)) {  // Skip space where trackpad is
                translate([
                    -laptop_x/2 + 10 + col * (key_width + spacing),
                    -laptop_y/2 + 15 + row * (key_height + spacing),
                    0
                ])
                cube([key_width, key_height, key_depth], center = true);
            }
        }
    }
}

// Trackpad
module trackpad() {
    translate([15, -laptop_y/2 + 10, laptop_z/2 + 0.1])
    color(color_trackpad)
    cube([25, 15, 0.5], center = true);
}

// USB ports
module usb_ports() {
    port_width = 7;
    port_height = 3;

    translate([-laptop_x/2 - 0.1, 15, 0])
    rotate([0, 90, 0])
    color(color_ports)
    for (i = [0:1]) {
        translate([0, i * 8, 0])
        cube([port_width, port_height, 2]);
    }
}

// HDMI port
module hdmi_port() {
    translate([-laptop_x/2 - 0.1, -10, 0])
    rotate([0, 90, 0])
    color(color_ports)
    cube([12, 5, 7]);
}

// Power connector
module power_port() {
    translate([-laptop_x/2 - 0.1, 0, 0])
    rotate([0, 90, 0])
    color(color_ports)
    cylinder(h = 8, r1 = 3, r2 = 3);
}

// Screen bezel
module screen_bezel() {
    color(color_base)
    difference() {
        // Outer frame
        translate([0, 0, laptop_z/2 + screen_thickness/2])
        cube([laptop_x - 2, laptop_y - 2, screen_thickness], center = true);

        // Screen cutout
        translate([0, 0, laptop_z/2 + screen_thickness/2 + 0.1])
        cube([laptop_x - 10, laptop_y - 10, screen_thickness + 0.2], center = true);
    }
}

// LCD panel
module lcd_panel() {
    color(color_screen)
    translate([0, 0, laptop_z/2 + screen_thickness/2])
    cube([laptop_x - 12, laptop_y - 12, 0.5], center = true);
}

// Hinge
module hinge() {
    color(color_base)
    translate([0, laptop_y/2 - 3, 0])
    cylinder(h = laptop_y - 6, r = 2, center = true);
}

// Logo
module logo() {
    color(color_logo)
    translate([0, 0, laptop_z/2 + screen_thickness - 0.2])
    cube([8, 12, 0.3], center = true);
}

// Status LEDs
module status_leds() {
    color([0, 1, 0])  // Green LED
    translate([laptop_x/2 - 5, laptop_y/2 - 3, laptop_z/2 + 0.1])
    cylinder(h = 0.5, r = 1);

    color([1, 1, 0])  // Yellow LED
    translate([laptop_x/2 - 5, laptop_y/2 - 6, laptop_z/2 + 0.1])
    cylinder(h = 0.5, r = 1);
}

// Assemble laptop
union() {
    // Base
    laptop_base();

    // Keyboard and trackpad
    keyboard();
    trackpad();

    // Ports
    usb_ports();
    hdmi_port();
    power_port();

    // Status LEDs
    status_leds();

    // Screen assembly
    translate([0, 0, laptop_z])
    rotate([30, 0, 0])  // Screen open at 30 degrees
    union() {
        screen_bezel();
        lcd_panel();
        logo();
    }

    // Hinges
    translate([laptop_x/2 - 8, 0, 0])
    hinge();
    translate([-laptop_x/2 + 8, 0, 0])
    hinge();
}

// OpenSCAD Laptop Model
// Realistic style, medium complexity

// Overall dimensions (mm)
laptop_width = 80;
laptop_depth = 50;
laptop_closed_height = 8;
laptop_open_angle = 30;

// Base thickness
base_thickness = 3;
screen_thickness = 2;

// Wall thickness for 3D printing
wall_thickness = 1.2;

// Colors
color_base = [0.2, 0.2, 0.2];
color_screen = [0.05, 0.05, 0.05];
color_keys = [0.3, 0.3, 0.3];
color_trackpad = [0.25, 0.25, 0.25];
color_ports = [0.4, 0.4, 0.4];

module laptop_base() {
    color(color_base)
    difference() {
        // Main base
        cube([laptop_width, laptop_depth, base_thickness], center=true);

        // Hollow inside for weight reduction
        translate([0, 0, -0.5])
        cube([laptop_width - 2*wall_thickness, laptop_depth - 2*wall_thickness, base_thickness], center=true);
    }
}

module keyboard() {
    // Keyboard area
    translate([0, 10, base_thickness/2 + 0.01])
    color(color_keys)
    cube([laptop_width - 10, 20, 0.3], center=true);

    // Individual keys
    for (x = [-30:6:30]) {
        for (y = [5:4:15]) {
            translate([x, y, base_thickness/2 + 0.4])
            color(color_keys)
            cube([5, 3, 0.2], center=true);
        }
    }
}

module trackpad() {
    translate([0, -10, base_thickness/2 + 0.01])
    color(color_trackpad)
    cube([20, 12, 0.2], center=true);
}

module ports() {
    // USB ports on left side
    for (i = [0:1]) {
        translate([-laptop_width/2 - 0.01, -10 + i*8, 0])
        rotate([0, 90, 0])
        color(color_ports)
        cube([4, 7, 2], center=true);
    }

    // Power port on right side
    translate([laptop_width/2 + 0.01, 0, 0])
    rotate([0, -90, 0])
    color(color_ports)
    cylinder(h=5, r=2.5, center=true);
}

module screen_bezel() {
    color(color_base)
    difference() {
        // Outer frame
        cube([laptop_width, laptop_depth, screen_thickness], center=true);

        // Screen cutout
        translate([0, 0, -0.5])
        cube([laptop_width - 6, laptop_depth - 6, screen_thickness], center=true);
    }
}

module screen_display() {
    translate([0, 0, screen_thickness/2 - 0.1])
    color(color_screen)
    cube([laptop_width - 8, laptop_depth - 8, 0.2], center=true);
}

module webcam() {
    translate([0, laptop_depth/2 - 3, screen_thickness/2 - 0.1])
    color([0.1, 0.1, 0.1])
    cylinder(h=0.5, r=1.5, center=true);
}

module ventilation_grills() {
    // Bottom ventilation
    for (x = [-20:5:20]) {
        translate([x, 0, -base_thickness/2 - 0.01])
        cube([3, laptop_depth - 10, 0.5], center=true);
    }
}

module hinges() {
    // Left hinge
    translate([-laptop_width/2 + 5, laptop_depth/2, 0])
    rotate([90, 0, 0])
    cylinder(h=3, r=1.5, center=true);

    // Right hinge
    translate([laptop_width/2 - 5, laptop_depth/2, 0])
    rotate([90, 0, 0])
    cylinder(h=3, r=1.5, center=true);
}

module laptop_screen() {
    translate([0, laptop_depth/2, base_thickness/2])
    rotate([laptop_open_angle, 0, 0])
    translate([0, laptop_depth/2, screen_thickness/2])
    union() {
        screen_bezel();
        screen_display();
        webcam();
    }
}

module status_leds() {
    // Power LED
    translate([-laptop_width/2 + 5, -laptop_depth/2 + 2, base_thickness/2 + 0.01])
    color([0, 1, 0])
    sphere(r=0.5);

    // Battery LED
    translate([-laptop_width/2 + 10, -laptop_depth/2 + 2, base_thickness/2 + 0.01])
    color([0, 0.5, 1])
    sphere(r=0.5);
}

// Main assembly
union() {
    laptop_base();
    keyboard();
    trackpad();
    ports();
    ventilation_grills();
    hinges();
    laptop_screen();
    status_leds();
}

// Realistic Tablet Model - OpenSCAD
// Dimensions in mm for 3D printing

// Tablet dimensions
tablet_length = 80;
tablet_width = 120;
tablet_thickness = 6;
screen_border = 4;
corner_radius = 5;

// Colors
color_body = "silver";
color_screen = "black";
color_button = "darkgray";
color_cam = "black";
color_port = "darkgray";

// Main body with rounded corners
module tablet_body() {
    color(color_body)
    difference() {
        // Main rounded body
        minkowski() {
            cube([tablet_length - 2*corner_radius, tablet_width - 2*corner_radius, tablet_thickness], center = true);
            sphere(r = corner_radius);
        }

        // Screen cutout
        translate([0, 0, tablet_thickness/2 - 0.5])
            cube([tablet_length - 2*screen_border, tablet_width - 2*screen_border, tablet_thickness], center = true);
    }
}

// Screen display
module screen() {
    color(color_screen)
    translate([0, 0, tablet_thickness/2 - 0.4])
        cube([tablet_length - 2*screen_border - 1, tablet_width - 2*screen_border - 1, 0.2], center = true);
}

// Home button
module home_button() {
    color(color_button)
    translate([0, -tablet_width/2 + 8, tablet_thickness/2 - 0.5])
        cylinder(h = 1, r = 4, $fn = 32);
}

// Volume buttons
module volume_buttons() {
    color(color_button)
    // Volume up
    translate([tablet_length/2 + 0.5, 15, tablet_thickness/4])
        cube([1, 10, 3], center = true);

    // Volume down
    translate([tablet_length/2 + 0.5, 0, tablet_thickness/4])
        cube([1, 10, 3], center = true);
}

// Power button
module power_button() {
    color(color_button)
    translate([tablet_length/2 + 0.5, tablet_width/2 - 15, tablet_thickness/4])
        cube([1, 15, 3], center = true);
}

// Camera
module camera() {
    color(color_cam)
    translate([0, tablet_width/2 - 8, tablet_thickness/2 - 0.5])
        cylinder(h = 0.5, r = 2.5, $fn = 16);

    // Camera lens detail
    translate([0, tablet_width/2 - 8, tablet_thickness/2 - 0.2])
        cylinder(h = 0.2, r = 1.5, $fn = 16);
}

// Speaker holes
module speakers() {
    for (i = [-2:1:2]) {
        color(color_body)
        translate([i * 6, -tablet_width/2 + 2, tablet_thickness/2 - 0.5])
            cylinder(h = 1, r = 0.8, $fn = 16);
    }
}

// USB-C port
module usb_port() {
    color(color_port)
    translate([0, -tablet_width/2 - 0.1, 0])
        cube([6, 1, 3], center = true);
}

// LED indicator
module led_indicator() {
    color("red")
    translate([tablet_length/2 - 8, tablet_width/2 - 2, tablet_thickness/2 - 0.5])
        cylinder(h = 0.5, r = 0.5, $fn = 8);
}

// Assemble the tablet
union() {
    tablet_body();
    screen();
    home_button();
    volume_buttons();
    power_button();
    camera();
    speakers();
    usb_port();
    led_indicator();
}

// Realistic Desktop Bluetooth Speaker
// Approx 85 mm tall, 85 mm wide, 80 mm deep

// Main body parameters
body_w = 80;
body_d = 75;
body_h = 45;
wall_t = 2.5;

// Grille parameters
grille_h = 25;
grille_d = 68;
bar_w = 1.5;
bar_gap = 3;

// Top control ring
ring_outer = 35;
ring_inner = 24;
ring_h = 8;

// Ports
usb_w = 8;
usb_h = 3.5;
usb_d = 5;
aux_w = 6;
aux_h = 3;

// Feet
foot_r = 5;
foot_h = 4;

// Colors
body_col = [0.1, 0.1, 0.1];
grille_col = [0.25, 0.25, 0.25];
accent_col = [0.8, 0.1, 0.15];

module main_body() {
    difference() {
        // Outer shell
        color(body_col)
        cube([body_w, body_d, body_h], center=true);

        // Hollow inside
        translate([0,0,-wall_t/2])
            cube([body_w-2*wall_t, body_d-2*wall_t, body_h], center=true);
    }
}

module speaker_grille() {
    translate([0, (body_d-grille_d)/2 - wall_t, body_h/2 - grille_h/2 - wall_t]) {
        // Grille frame
        color(grille_col)
        difference() {
            cube([body_w-2*wall_t, grille_d, grille_h], center=true);
            translate([0,0,1])
                cube([body_w-2*wall_t-2*wall_t, grille_d-2*wall_t, grille_h], center=true);
        }

        // Grille bars
        for(x = [-((body_w-2*wall_t)/2 - bar_w/2) : bar_gap : ((body_w-2*wall_t)/2 - bar_w/2)]) {
            color(grille_col)
            translate([x, 0, 0])
                cube([bar_w, grille_d, grille_h], center=true);
        }
    }
}

module control_ring() {
    translate([0, 0, body_h/2 + ring_h/2 - wall_t]) {
        color(accent_col)
        difference() {
            cylinder(h=ring_h, r=ring_outer, center=true);
            cylinder(h=ring_h+1, r=ring_inner, center=true);
        }

        // Center button
        color([0.9,0.9,0.9])
        translate([0,0,ring_h/2+0.5])
            cylinder(h=2, r=ring_inner/2, center=true);
    }
}

module ports() {
    // USB-C port
    translate([body_w/2 - usb_w/2 - wall_t, 0, -body_h/2 + usb_h*2]) {
        color([0,0,0])
        rotate([0,90,0])
            cube([usb_w, usb_h, usb_d], center=true);
    }

    // 3.5mm aux port
    translate([body_w/2 - aux_w/2 - wall_t, 15, -body_h/2 + usb_h*2]) {
        color([0,0,0])
        rotate([0,90,0])
            cube([aux_w, aux_h, aux_h*2], center=true);
    }
}

module indicator_led() {
    translate([body_w/2 - 5, -body_d/2 + 5, body_h/2 - 3]) {
        color([0,0.8,0])
        sphere(r=1.5, center=true);
    }
}

module rubber_feet() {
    module foot() {
        color([0.1,0.1,0.1])
        cylinder(h=foot_h, r=foot_r, center=true);
    }

    positions = [
        [-body_w/2 + foot_r*1.5, -body_d/2 + foot_r*1.5, -body_h/2 - foot_h/2],
        [ body_w/2 - foot_r*1.5, -body_d/2 + foot_r*1.5, -body_h/2 - foot_h/2],
        [-body_w/2 + foot_r*1.5,  body_d/2 - foot_r*1.5, -body_h/2 - foot_h/2],
        [ body_w/2 - foot_r*1.5,  body_d/2 - foot_r*1.5, -body_h/2 - foot_h/2]
    ];

    for(pos = positions) {
        translate(pos)
            foot();
    }
}

// Logo placeholder
module logo() {
    translate([0, 0, body_h/2 + ring_h + 1]) {
        color([0.9,0.9,0.9])
        linear_extrude(height=0.5)
            text("BTX", size=6, halign="center", valign="center");
    }
}

// Assembly
union() {
    main_body();
    speaker_grille();
    control_ring();
    ports();
    indicator_led();
    rubber_feet();
    logo();
}

// Server 1U rack-mount case (realistic style, medium complexity)
// 1U height = 44.45 mm, scaled to ~70 mm length for printing

// Color definitions
color_case = "gray20";
color_front = "gray10";
color_hdd = "silver";
color_fan = "gray30";
color_led = "red";
color_usb = "blue";
color_ethernet = "yellow";

// Overall dimensions
case_w = 70;
case_h = 44;
case_d = 40;

// Wall thickness
wall = 2;

// Main case body
module server_case() {
    difference() {
        // Outer shell
        color(color_case)
            cube([case_w, case_h, case_d], center=false);

        // Hollow interior
        translate([wall, wall, wall])
            cube([case_w-2*wall, case_h-2*wall, case_d-wall], center=false);
    }
}

// Front bezel with vents and ports
module front_bezel() {
    difference() {
        // Front plate
        color(color_front)
            translate([0, 0, case_d-2])
            cube([case_w, case_h, 2]);

        // Power button
        translate([case_w*0.1, case_h*0.5, case_d-1.5])
        cylinder(h=2, d=6);

        // LED holes
        for(i=[0:2]) {
            translate([case_w*0.2 + i*8, case_h*0.2, case_d-1])
            cylinder(h=2, d=3);
        }

        // USB ports
        for(i=[0:1]) {
            translate([case_w*0.7 + i*10, case_h*0.3, case_d-1])
            cube([8, 12, 2]);
        }

        // Ethernet ports
        for(i=[0:1]) {
            translate([case_w*0.7 + i*10, case_h*0.7, case_d-1])
            cube([12, 8, 2]);
        }

        // Ventilation slots
        for(i=[0:4]) {
            translate([case_w*0.15, case_h*0.1 + i*8, case_d-1])
            cube([case_w*0.4, 2, 2]);
        }
    }
}

// Hard drive caddies
module hdd_caddy(pos) {
    translate([pos, case_h*0.3, case_d*0.7])
    color(color_hdd)
    difference() {
        // Drive body
        cube([35, 8, 25]);

        // Handle
        translate([32, 4, 20])
        cube([8, 1, 3]);

        // Screw holes
        for(x=[5:15:25])
            for(z=[5:15:15]) {
                translate([x, 4, z])
                cylinder(h=10, d=1, center=true);
            }
    }
}

// Cooling fan
module fan() {
    translate([case_w*0.7, case_h*0.5, case_d*0.3])
    color(color_fan)
    difference() {
        // Fan frame
        cylinder(h=10, d=30);

        // Center hub
        translate([0, 0, -1])
        cylinder(h=12, d=8);

        // Blades (7 blades)
        for(i=[0:6]) {
            rotate([0, 0, i*51.4])
            translate([0, 6, 5])
            cube([20, 2, 10], center=true);
        }

        // Mounting holes
        for(i=[0:3]) {
            rotate([0, 0, i*90])
            translate([10, 0, 5])
            cylinder(h=10, d=3);
        }
    }
}

// Rear IO shield
module rear_io() {
    translate([0, 0, 0])
    difference() {
        // IO plate
        color(color_front)
        cube([case_w, case_h, 2]);

        // Power connector
        translate([case_w*0.1, case_h*0.5, 1])
        cube([15, 15, 2]);

        // VGA port
        translate([case_w*0.4, case_h*0.7, 1])
        cube([12, 10, 2]);

        // More ethernet ports
        for(i=[0:3]) {
            translate([case_w*0.6 + i*8, case_h*0.3, 1])
            cube([6, 8, 2]);
        }
    }
}

// Status LEDs
module status_leds() {
    for(i=[0:2]) {
        translate([case_w*0.2 + i*8, case_h*0.2, case_d-1])
        color(color_led)
        cylinder(h=1, d=3);
    }

    translate([case_w*0.1, case_h*0.5, case_d-0.5])
    color("green")
    cylinder(h=1, d=6);
}

// Assembly
server_case();
front_bezel();
hdd_caddy(case_w*0.2);
hdd_caddy(case_w*0.6);
fan();
translate([0, 0, -2])
rear_io();
status_leds();

// USB port coloring
translate([case_w*0.7, case_h*0.3, case_d-1])
color(color_usb)
cube([8, 12, 1]);

translate([case_w*0.8, case_h*0.3, case_d-1])
color(color_usb)
cube([8, 12, 1]);

// Ethernet port coloring
for(i=[0:3]) {
    translate([case_w*0.6 + i*8, case_h*0.3, -1])
    color(color_ethernet)
    cube([6, 8, 1]);
}

// Professional audio workstation - mixer-style interface
// 3D-printable model with no overhangs

// Main dimensions
base_width = 80;
base_depth = 60;
base_height = 8;
panel_thickness = 2;

// Screen dimensions
screen_width = 40;
screen_height = 25;
screen_depth = 3;
screen_offset = 10;

// Knob dimensions
knob_radius = 3;
knob_height = 4;
knob_rows = 3;
knob_cols = 4;

// Fader slot dimensions
fader_width = 30;
fader_height = 2;
fader_depth = 3;

// Port dimensions
port_radius = 2.5;
port_height = 4;

// Main base
union() {
    // Base enclosure
    color([0.1, 0.1, 0.1]) 
    difference() {
        cube([base_width, base_depth, base_height], center=true);
        translate([0, 0, base_height/2 - panel_thickness])
            cube([base_width-2, base_depth-2, panel_thickness+1], center=true);
    }

    // Top control panel
    color([0.15, 0.15, 0.15])
    translate([0, 0, base_height/2 + panel_thickness/2])
        cube([base_width, base_depth, panel_thickness], center=true);

    // LCD screen
    color([0.05, 0.05, 0.05])
    translate([0, screen_offset, base_height/2 + panel_thickness + screen_depth/2])
        cube([screen_width, screen_height, screen_depth], center=true);

    // Screen bezel
    color([0.2, 0.2, 0.2])
    translate([0, screen_offset, base_height/2 + panel_thickness + screen_depth/2])
        cube([screen_width+4, screen_height+4, screen_depth/2], center=true);

    // Display content (simplified waveform)
    color([0, 1, 0.2])
    for(i = [-15:5:15]) {
        translate([i, screen_offset, base_height/2 + panel_thickness + screen_depth + 0.1])
            cube([2, abs(sin(i*5)*8) + 2, 0.2], center=true);
    }

    // LED indicators above screen
    module led_indicator(x_pos) {
        color([0.8, 0, 0])
        translate([x_pos, screen_offset + screen_height/2 + 5, base_height/2 + panel_thickness + 1])
            cylinder(r=1.5, h=1, $fn=8);
    }

    for(x = [-15, -5, 5, 15]) {
        led_indicator(x);
    }

    // Control knobs grid
    module control_knob(x, y) {
        color([0.7, 0.7, 0.7])
        translate([x, y, base_height/2 + panel_thickness + knob_height/2])
            cylinder(r=knob_radius, h=knob_height, $fn=16);
        color([0.3, 0.3, 0.3])
        translate([x, y, base_height/2 + panel_thickness + knob_height + 0.1])
            cube([1, knob_radius*1.6, 0.5], center=true);
    }

    // Knob grid layout
    for(row = [0:knob_rows-1]) {
        for(col = [0:knob_cols-1]) {
            control_knob(
                -base_width/3 + col * (base_width/4),
                -base_depth/3 + row * (base_depth/4)
            );
        }
    }

    // Fader slot
    color([0.05, 0.05, 0.05])
    translate([0, -screen_offset - 5, base_height/2 + panel_thickness + fader_depth/2])
        cube([fader_width, fader_height, fader_depth], center=true);

    // Fader cap
    color([0.8, 0.1, 0.1])
    translate([0, -screen_offset - 5, base_height/2 + panel_thickness + fader_depth + 2])
        cube([6, 10, 3], center=true);

    // Input/output ports on back
    module audio_port(x, y) {
        color([0.2, 0.2, 0.2])
        translate([x, y, -base_height/2 - port_height/2])
            cylinder(r=port_radius, h=port_height, $fn=12);
        color([0.05, 0.05, 0.05])
        translate([x, y, -base_height/2 - port_height - 0.1])
            cylinder(r=port_radius*0.7, h=0.5, $fn=8);
    }

    // Back panel ports
    for(i = [-3:1:3]) {
        audio_port(base_width/2 - 8, i * 6);
    }

    // USB port
    color([0.15, 0.15, 0.15])
    translate([base_width/2 - 8, 0, -base_height/2 - 2])
        cube([7, 3, 4], center=true);

    // Brand label
    color([0.8, 0.8, 0.8])
    translate([0, base_depth/2 - 2, base_height/2 + panel_thickness + 1])
        cube([25, 8, 0.5], center=true);

    // Power LED
    color([0, 1, 0])
    translate([base_width/2 - 5, base_depth/2 - 5, base_height/2 + panel_thickness + 1])
        cylinder(r=2, h=1, $fn=8);

    // Rubber feet
    module foot(x, y) {
        color([0.05, 0.05, 0.05])
        translate([x, y, -base_height/2 - 1])
            cylinder(r=3, h=2, $fn=12);
    }

    foot(base_width/2 - 5, base_depth/2 - 5);
    foot(-base_width/2 + 5, base_depth/2 - 5);
    foot(base_width/2 - 5, -base_depth/2 + 5);
    foot(-base_width/2 + 5, -base_depth/2 + 5);
}

