    translate([phone_length/2 + button_thickness/2, -phone_width/4, phone_thickness/4]) {
        color([0.3, 0.3, 0.3])  // Gray buttons
        cube([button_thickness, button_width, button_height], center=true);
    }

    // Volume buttons
    translate([phone_length/2 + button_thickness/2, phone_width/4, phone_thickness/4 + 5]) {
        color([0.3, 0.3, 0.3])
        cube([button_thickness, button_width, button_height], center=true);
    }

    translate([phone_length/2 + button_thickness/2, phone_width/4, phone_thickness/4 - 5]) {
        color([0.3, 0.3, 0.3])
        cube([button_thickness, button_width, button_height], center=true);
    }
}

// Create charging port
module charging_port() {
    translate([0, -phone_width/2 - 0.1, -phone_thickness/2 + port_height/2 + 1]) {
        color([0.1, 0.1, 0.1])
        cube([port_width, 0.2, port_height], center=true);
    }
}

// Create speaker grill
module speaker_grill() {
    translate([0, -phone_width/2 - 0.1, -phone_thickness/2 + 2]) {
        for (i = [-speaker_grill_length/2 + speaker_hole_spacing : speaker_hole_spacing : speaker_grill_length/2 - speaker_hole_spacing]) {
            translate([i, 0, 0]) {
                color([0.1, 0.1, 0.1])
                cylinder(h=0.5, r=speaker_hole_diameter/2, center=true);
            }
        }
    }
}

// Create front-facing camera
module front_camera() {
    translate([phone_length/2 - 10, phone_width/2 - 5, phone_thickness/2 + screen_thickness + 0.1]) {
        color([0, 0, 0])
        cylinder(h=0.5, r=1.5, center=true);
    }
}

// Create earpiece speaker
module earpiece() {
    translate([phone_length/2 - 20, phone_width/2 - 2, phone_thickness/2 + screen_thickness + 0.1]) {
        color([0.1, 0.1, 0.1])
        cube([10, 1, 0.5], center=true);
    }
}

// Create home button (virtual)
module home_button() {
    translate([0, -phone_width/2 + 5, phone_thickness/2 + screen_thickness + 0.1]) {
        color([0.15, 0.15, 0.15])
        difference() {
            cube([8, 2, 0.5], center=true);
            cube([7.5, 1.5, 0.6], center=true);
        }
    }
}

// Assemble the phablet
union() {
    phablet_body();
    screen();
    camera_bump();
    camera_lens();
    side_buttons();
    charging_port();
    speaker_grill();
    front_camera();
    earpiece();
    home_button();
}

