// Smartwatch model with realistic styling
// Dimensions based on typical 44mm watch

// Color definitions
watch_body_color = [0.15,0.15,0.15];  // Dark gray aluminum
screen_color = [0,0,0];               // Black screen
button_color = [0.8,0.8,0.8];         // Silver buttons
band_color = [0.1,0.1,0.1];           // Black fluoroelastomer
sensor_color = [0.9,0.9,0.9];         // Light gray sensors

// Main dimensions
watch_width = 38;
watch_height = 44;
watch_thickness = 10.7;
screen_width = 32;
screen_height = 38;
screen_thickness = 0.5;
wall_thickness = 2;

// Create complete smartwatch
module smartwatch() {
    // Watch body
    color(watch_body_color)
    difference() {
        // Main body - rounded rectangle
        minkowski() {
            cube([watch_width, watch_height, watch_thickness], center=true);
            sphere(2.5);
        }

        // Hollow out for electronics
        translate([0,0,-1])
        minkowski() {
            cube([watch_width-wall_thickness*2, watch_height-wall_thickness*2, watch_thickness], center=true);
            sphere(2);
        }

        // Screen cutout
        translate([0,0,watch_thickness/2])
        cube([screen_width+1, screen_height+1, screen_thickness*2], center=true);
    }

    // Screen
    color(screen_color)
    translate([0,0,watch_thickness/2+0.1])
    cube([screen_width, screen_height, screen_thickness], center=true);

    // Digital Crown
    color(button_color)
    rotate([90,0,0])
    translate([watch_width/2+2, 0, 0])
    cylinder(h=3, d1=4, d2=3.5, center=true);

    // Side button
    color(button_color)
    translate([watch_width/2+1, watch_height/4, 0])
    cube([2, 6, 2], center=true);

    // Speaker holes (left side)
    for(i=[-2:1:2]) {
        color(watch_body_color)
        translate([-watch_width/2-0.5, i*4, -watch_thickness/4])
        rotate([0,90,0])
        cylinder(h=1, d=0.8);
    }

    // Watch band attachment points
    translate([0, watch_height/2+5, 0])
    band_attachment();
    mirror([0,1,0])
    translate([0, watch_height/2+5, 0])
    band_attachment();

    // Heart rate sensor array (bottom)
    color(sensor_color)
    translate([0,0,-watch_thickness/2])
    difference() {
        cylinder(h=0.5, d=8, center=true);
        for(i=[-1:1:1]) {
            for(j=[-1:1:1]) {
                translate([i*2.5, j*2.5, -0.1])
                cylinder(h=0.7, d=0.8);
            }
        }
    }

    // Wireless charging coil indicator
    color([0.2,0.2,0.2])
    translate([0,0,-watch_thickness/2+0.3])
    difference() {
        cylinder(h=0.1, d=20, center=true);
        cylinder(h=0.2, d=18, center=true);
    }
}

// Band attachment module
module band_attachment() {
    color(watch_body_color)
    difference() {
        // Attachment lug
        translate([0,0,0])
        cube([watch_width-4, 10, 6], center=true);

        // Band slot
        translate([0,0,0])
        cube([watch_width-6, 12, 2], center=true);
    }

    // Watch band
    color(band_color)
    translate([0,5,0])
    cube([watch_width-6, 20, 2], center=true);
}

// Assemble the watch
smartwatch();

// Smartband electronic device for 3D printing
// Main dimensions (mm)
band_width = 18;
band_thickness = 3;
screen_width = 12;
screen_height = 10;
screen_depth = 1;
body_length = 40;
body_height = 12;

// Colors
band_color = "LightGray";
body_color = "DarkSlateGray";
screen_color = "Black";
button_color = "Silver";
sensor_color = "Crimson";

// Main body module
module smartband_body() {
    color(body_color) {
        // Main rectangular body
        translate([0, 0, band_thickness/2])
            cube([body_length, band_width, body_height], center=true);

        // Rounded top edges
        for(y = [-band_width/2+1, band_width/2-1]) {
            translate([body_length/2-1, y, band_thickness/2+body_height/2-1])
                rotate([0, 90, 0])
                    cylinder(h=2, r=1, $fn=16);
            translate([-body_length/2+1, y, band_thickness/2+body_height/2-1])
                rotate([0, 90, 0])
                    cylinder(h=2, r=1, $fn=16);
        }
    }
}

// Display screen module
module display_screen() {
    color(screen_color) {
        translate([0, 0, band_thickness/2+body_height/2-screen_depth/2])
            cube([screen_width, screen_height, screen_depth], center=true);
    }

    // Screen bevel
    color("Gray")
    translate([0, 0, band_thickness/2+body_height/2-screen_depth/2-0.1])
        cube([screen_width+2, screen_height+2, 0.2], center=true);
}

// Side button module
module side_button() {
    color(button_color) {
        translate([body_length/2+1, 0, band_thickness/2+body_height/2-2])
            cylinder(h=2, r=1.5, $fn=16);
    }
}

// Heart rate sensor module
module heart_sensor() {
    color(sensor_color) {
        translate([0, 0, band_thickness/2-1])
            cylinder(h=0.5, r=3, $fn=32);

        // LED indicators
        for(i = [-1, 1]) {
            translate([i*1.5, 0, band_thickness/2-1.2])
                cylinder(h=0.2, r=0.5, $fn=16);
        }
    }
}

// Charging port module
module charging_port() {
    color("Black")
    translate([-body_length/2-0.5, 0, band_thickness/2-body_height/2+2])
        rotate([0, 90, 0])
            cylinder(h=2, r=2, $fn=16);

    // Port detail
    difference() {
        translate([-body_length/2-0.5, 0, band_thickness/2-body_height/2+2])
            cube([4, 4, 2], center=true);
        translate([-body_length/2-0.5, 0, band_thickness/2-body_height/2+2])
            cube([3.5, 3.5, 2.5], center=true);
    }
}

// Band strap module
module strap_segment(length, is_top=true) {
    color(band_color) {
        // Tapered strap
        hull() {
            translate([0, 0, 0])
                cylinder(h=band_thickness, r=band_width/2, $fn=32);
            translate([length, 0, 0])
                cylinder(h=band_thickness, r=band_width/3, $fn=32);
        }

        // Holes for sizing
        if(is_top) {
            for(i = [1:2:10]) {
                translate([length - i*5, 0, band_thickness/2])
                    cylinder(h=band_thickness+1, r=1.5, $fn=16);
            }
        }

        // Buckle attachment
        if(!is_top) {
            translate([length, 0, band_thickness/2])
                rotate([90, 0, 0])
                    cylinder(h=band_width/2, r=1.5, $fn=16);
        }
    }
}

// Assembly
union() {
    // Main body
    smartband_body();

    // Display
    display_screen();

    // Buttons
    side_button();

    // Sensor
    heart_sensor();

    // Charging port
    charging_port();

    // Top strap
    translate([body_length/2+15, 0, 0])
        strap_segment(50, true);

    // Bottom strap
    translate([-body_length/2-15, 0, 0])
        rotate([0, 180, 0])
            strap_segment(40, false);
}

