module rounded_box(s, r) {
    hull() {
        for(dx=[-1,1], dy=[-1,1], dz=[-1,1])
            translate([dx*(s.x/2-r), dy*(s.y/2-r), dz*(s.z/2-r)])
                sphere(r=r);
    }
}

module lens(w, h, d) {
    difference() {
        color(color_lens)
            scale([1,0.7,1])
                rotate([0,90,0])
                    cylinder(h=w, d=h, $fn=60);
        // anti-glare chamfer
        translate([d/2,0,0])
            rotate([0,90,0])
                cylinder(h=w*1.2, d1=h*0.9, d2=h*1.1, $fn=60);
    }
}

