    rotate([0, angle, 0])
    translate([60, 0, 15])
    color(color_antenna)
    union() {
        // Antenna base
        cylinder(h=5, r=5, center=true);

        // Antenna rod
        translate([0, 0, 10])
        rotate([90, 0, 0])
        cylinder(h=30, r=1.5, center=false);
    }
}

// LED indicators
module leds() {
    for(pos = [-40, -20, 0, 20, 40]) {
        translate([pos, 40, 15])
        color(color_led)
        sphere(r=1.5);
    }
}

// Ethernet ports
module ethernet_ports() {
    color(color_ports)
    for(i = [0:3]) {
        translate([-45 + i*15, -40, 0])
        difference() {
            cube([10, 8, 8], center=true);
            translate([0, -4, 0])
            cube([8, 2, 6], center=true);
        }
    }
}

// Power port
module power_port() {
    color(color_ports)
    translate([45, -40, 0])
    difference() {
        cube([12, 8, 8], center=true);
        translate([0, -4, 0])
        cylinder(h=2, r=4, center=true);
    }
}

// Reset button
