// Realistic 3D printable light switch module
// Overall dimensions: 54mm x 54mm x 34mm

// Color constants
$fn = 50;

// Main housing module
module housing() {
    difference() {
        // Main body
        color("ivory") cube([54, 54, 34], center=true);

        // Mounting box cavity
        color("ivory") translate([0, 0, 5]) cube([48, 48, 28], center=true);

        // Screw holes for mounting
        for (x = [-21, 21])
            for (y = [-21, 21])
                translate([x, y, 0]) cylinder(h=40, d=3.5, center=true);
    }
}

// Switch paddle module
module paddle() {
    // Pivot axis
    translate([0, 0, 10]) rotate([90, 0, 0]) cylinder(h=50, d=3, center=true);

    // Upper paddle
    translate([0, 0, 27]) rotate([15, 0, 0]) {
        // Main paddle body
        color("white") cube([36, 22, 4], center=true);

        // Indicator ridge
        color("ivory") translate([0, 0, 2.1]) cube([30, 18, 0.5], center=true);
    }

    // Lower paddle
    translate([0, 0, 13]) rotate([-15, 0, 0]) {
        color("white") cube([36, 22, 4], center=true);
        color("ivory") translate([0, 0, 2.1]) cube([30, 18, 0.5], center=true);
    }
}

// Screws for faceplate
module screws() {
    color("silver") {
        for (x = [-24, 24])
            for (y = [-24, 24])
                translate([x, y, 17]) cylinder(h=4, d=6, $fn=6);
    }
}

// Faceplate
module faceplate() {
