// Bridge Electronic Device - Ethernet Switch
// Realistic style, medium complexity

// Overall dimensions (mm)
switch_length = 80;
switch_width = 50;
switch_height = 20;
wall_thickness = 1.5;

// Colors
color_case = [0.15, 0.15, 0.15];
color_ports = [0.8, 0.8, 0.8];
color_leds = [0.1, 0.9, 0.1];
color_label = [0.9, 0.9, 0.9];
color_screen = [0.05, 0.05, 0.05];

// Main case
module main_case() {
    color(color_case) difference() {
        // Outer shell
        cube([switch_length, switch_width, switch_height], center=true);

        // Hollow out inside
        translate([0, 0, -wall_thickness/2])
            cube([switch_length-wall_thickness*2, switch_width-wall_thickness*2, switch_height-wall_thickness], center=true);

        // Ventilation slots on top
        for (i = [-35:10:35]) {
            translate([i, 0, switch_height/2-0.1])
                cube([8, switch_width-10, 1]);
        }

        // Label recess
        translate([0, switch_width/2-8, switch_height/2-2])
            cube([40, 15, 1], center=true);
    }
}

// Ethernet port
module ethernet_port(x_pos) {
    translate([x_pos, -switch_width/2+5, 0]) {
        color(color_ports) {
            // Port housing
            cube([12, 8, 10], center=true);

            // Port opening
            difference() {
                cube([10, 6, 8], center=true);
                translate([0, 2, 0])
                    cube([8, 4, 6], center=true);
            }

            // LED hole
            translate([0, -3.5, 4])
                rotate([90, 0, 0])
                    cylinder(h=1, d=2, $fn=16);
        }

        // LED
        translate([0, -3.5, 4])
            color(color_leds)
                sphere(d=1.5, $fn=16);
    }
}

// Power connector
module power_connector() {
    translate([switch_length/2-7, 0, 0]) {
        color(color_ports)
            difference() {
                cube([10, 10, 6], center=true);
                translate([0, 0, 0])
                    cube([6, 6, 8], center=true);
            }
    }
}

// Reset button
module reset_button() {
    translate([switch_length/2-15, switch_width/2-8, switch_height/2-2]) {
        color(color_ports)
            cylinder(h=2, d=4, center=true, $fn=20);
    }
}

// Activity LEDs strip
module activity_leds() {
    translate([0, 0, switch_height/2-1]) {
        color(color_screen)
            cube([switch_length-20, 3, 0.5], center=true);

        for (i = [-30:8:30]) {
            translate([i, 0, 0.3])
                color(color_leds)
                    sphere(d=1, $fn=16);
        }
    }
}

// Rubber feet
module rubber_feet() {
    for (x = [-30, 30])
        for (y = [-20, 20]) {
            translate([x, y, -switch_height/2-1])
                color([0.1, 0.1, 0.1])
                    cylinder(h=2, d=5, $fn=20);
        }
}

// Label text area
module label() {
    translate([0, switch_width/2-8, switch_height/2-1]) {
        color(color_label)
            cube([38, 13, 0.5], center=true);
    }
}

// Assemble the switch
