// OpenSCAD Firewall Electronic Device
// Medium complexity, realistic style

// Overall dimensions
device_w = 80;
device_h = 30;
device_d = 100;

// Wall thickness
wall_t = 2;

// Colors
color_case = [0.2, 0.2, 0.2];
color_front = [0.15, 0.15, 0.15];
color_ports = [0.3, 0.3, 0.3];
color_leds = [0, 1, 0];
color_display = [0.05, 0.05, 0.05];

// Main case
module main_case() {
    difference() {
        // Outer shell
        color(color_case)
        cube([device_w, device_h, device_d], center=true);

        // Hollow center
        translate([0, 0, 0])
        cube([device_w-wall_t*2, device_h-wall_t*2, device_d-wall_t], center=true);
    }
}

// Front panel with ports
module front_panel() {
    translate([0, 0, device_d/2 - wall_t/2]) {
        difference() {
            // Front face
            color(color_front)
            cube([device_w, device_h, wall_t], center=true);

            // Port cutouts
            for(i=[0:3]) {
                translate([device_w/2 - 15 - i*15, 0, 0])
                cube([8, 8, wall_t+0.1], center=true);
            }

            // LED indicators
            for(i=[0:4]) {
                translate([device_w/2 - 30 - i*8, device_h/2 - 3, 0])
                cylinder(h=wall_t+0.1, r=1.5, center=true);
            }
        }

        // Port connectors
        for(i=[0:3]) {
            translate([device_w/2 - 15 - i*15, 0, wall_t/2+0.5])
            color(color_ports)
            cube([6, 6, 3], center=true);
        }

        // LED housings
        for(i=[0:4]) {
            translate([device_w/2 - 30 - i*8, device_h/2 - 3, wall_t/2])
            color(color_leds)
            cylinder(h=1, r=1.5, center=true);
        }
    }
}

// Rear panel with power and management ports
module rear_panel() {
    translate([0, 0, -device_d/2 + wall_t/2]) {
        difference() {
            // Rear face
            color(color_front)
            cube([device_w, device_h, wall_t], center=true);

            // Power port
            translate([device_w/2 - 10, 0, 0])
            cube([12, 6, wall_t+0.1], center=true);

            // Management port
            translate([-device_w/2 + 15, 0, 0])
            cube([8, 8, wall_t+0.1], center=true);

            // Ventilation holes
            for(row=[0:1]) {
                for(col=[0:4]) {
                    translate([-device_w/2 + 15 + col*10, -device_h/2 + 5 + row*10, 0])
                    cylinder(h=wall_t+0.1, r=1.5, center=true);
                }
            }
        }

        // Power connector
        translate([device_w/2 - 10, 0, -wall_t/2-1])
        color(color_ports)
        cube([10, 4, 2], center=true);

        // Management port connector
        translate([-device_w/2 + 15, 0, -wall_t/2-1])
        color(color_ports)
        cube([6, 6, 2], center=true);
    }
}

// Top display area
module top_display() {
    translate([0, device_h/2 - wall_t, 0]) {
        difference() {
            color(color_display)
            cube([device_w - 20, 2, 30], center=true);

            // Status LEDs
            for(i=[0:2]) {
                translate([-device_w/3 + i*15, 0, device_d/4])
                cylinder(h=2.1, r=2, center=true);
            }
        }

        // LED lights
        for(i=[0:2]) {
            translate([-device_w/3 + i*15, 0, device_d/4])
            color([1, 0.3, 0])
            cylinder(h=1, r=1.5, center=true);
        }
    }
}

// Ventilation slots on sides
module side_ventilation() {
    for(side=[-1, 1]) {
        translate([side*(device_w/2 - wall_t/2), 0, 0]) {
            rotate([0, 90, 0]) {
                for(i=[0:8]) {
                    translate([0, -device_h/2 + 5 + i*6, 0])
                    cube([device_h-10, 1, wall_t], center=true);
                }
            }
        }
    }
}

// Mounting brackets
module mounting_brackets() {
    for(side=[-1, 1]) {
        translate([side*(device_w/2 + 5), 0, device_d/2 - 10]) {
            color(color_case)
            difference() {
                cube([10, device_h - 10, 5], center=true);
                cylinder(h=6, r=2, center=true);
            }
        }
    }
}

// Assemble the firewall
main_case();
front_panel();
rear_panel();
top_display();
side_ventilation();
mounting_brackets();

// Gateway Electronic Device - Realistic Style, Medium Complexity
// Dimensions: 120x80x30mm

// Main body dimensions
body_w = 120;
body_d = 80;
body_h = 25;

// Wall thickness for 3D printing
wall_t = 2;

// Corner radius
corner_r = 5;

// Module for rounded cube (main body)
module rounded_cube(size, r, center=false) {
    [w, d, h] = size;
    offset = center ? [-w/2, -d/2, -h/2] : [0, 0, 0];

    translate(offset) {
        hull() {
            for (x = [r, w-r])
                for (y = [r, d-r])
                    for (z = [r, h-r])
                        translate([x, y, z]) sphere(r=r);
        }
    }
}

// Main body
module main_body() {
    difference() {
        color("#2E2E2E") rounded_cube([body_w, body_d, body_h], corner_r);

        // Hollow interior
        translate([wall_t, wall_t, wall_t])
            rounded_cube([body_w-2*wall_t, body_d-2*wall_t, body_h], corner_r-wall_t);
    }
}

// Ventilation slots on top
module vents() {
    slot_w = 30;
    slot_d = 2;
    slot_h = 1;
    spacing = 5;

    for (i = [0:4]) {
        translate([body_w/2 - slot_w/2, 10 + i*(slot_d + spacing), body_h-0.5])
            color("#1A1A1A") cube([slot_w, slot_d, slot_h]);
    }
}

// LED indicators
module leds() {
    led_r = 2;
    spacing = 8;

    for (i = [0:2]) {
        translate([body_w - 15 - i*spacing, body_d - 8, body_h-0.5])
            color("#00FF00") cylinder(h=1.5, r=led_r, $fn=16);
    }
}

// Ethernet ports
module ethernet_ports() {
    port_w = 15;
    port_h = 12;
    port_d = 8;
    spacing = 18;

    for (i = [0:3]) {
        translate([10 + i*spacing, 0, body_h/2])
            difference() {
                color("#404040") cube([port_w, port_d, port_h]);
                translate([wall_t, port_d-wall_t, wall_t])
                    cube([port_w-2*wall_t, wall_t, port_h-2*wall_t]);
            }
    }
}

// Power connector
module power_port() {
    port_r = 5;
    port_d = 8;

    translate([body_w/2, 0, body_h/2])
        difference() {
            color("#404040") cylinder(h=port_d, r=port_r, $fn=32);
            cylinder(h=port_d, r=port_r-wall_t, $fn=32);
        }
}

// Reset button
module reset_button() {
    btn_r = 3;

    translate([body_w - 15, 0, body_h/2])
        color("#FF0000") cylinder(h=3, r=btn_r, $fn=16);
}

// WiFi antenna connectors
module antenna_connectors() {
    conn_r = 4;
    conn_h = 5;
    spacing = 25;

    for (i = [0:1]) {
        translate([body_w - 20 + i*spacing, body_d, body_h-10])
            difference() {
                color("#888888") cylinder(h=conn_h, r=conn_r, $fn=16);
                cylinder(h=conn_h, r=conn_r-wall_t, $fn=16);
            }
    }
}

// Status display
module status_display() {
    screen_w = 40;
    screen_h = 15;

    translate([body_w/2 - screen_w/2, body_d - 2, body_h-0.5])
        color("#000080") cube([screen_w, 1, screen_h]);
}

// Rubber feet
module rubber_feet() {
    foot_r = 3;
    foot_h = 2;

    for (x = [10, body_w-10])
        for (y = [10, body_d-10])
            translate([x, y, -foot_h])
                color("#333333") cylinder(h=foot_h, r=foot_r, $fn=16);
}

// Assembly
main_body();
translate([0, 0, 0]) vents();
translate([0, 0, 0]) leds();
translate([0, 0, 0]) ethernet_ports();
translate([0, 0, 0]) power_port();
translate([0, 0, 0]) reset_button();
