// Microprocessor Development Board - Arduino Uno Style
// Medium complexity realistic model for 3D printing

// Main board dimensions
board_width = 68;
board_height = 54;
board_thickness = 1.6;
board_color = "darkgreen";

// Component dimensions
ic_width = 14;
ic_length = 14;
ic_height = 3;
usb_width = 12;
usb_height = 8;
usb_depth = 7;
power_jack_d = 10;
power_jack_h = 8;

// Pin header settings
pin_rows = 2;
pin_cols = 10;
pin_spacing = 2.54;
pin_height = 8;
pin_diameter = 0.64;

module board() {
    color(board_color)
    difference() {
        cube([board_width, board_height, board_thickness], center=true);

        // Mounting holes
        for (x = [-board_width/2 + 3, board_width/2 - 3])
            for (y = [-board_height/2 + 3, board_height/2 - 3])
                translate([x, y, 0])
                    cylinder(h=board_thickness+1, d=3, center=true);

        // USB cutout
        translate([board_width/2 - 3, 0, board_thickness/2])
            cube([usb_depth+1, usb_width+2, board_thickness], center=true);
    }
}

module pin_header(x, y, cols, rows, angle=0) {
    color("silver")
    rotate([0, 0, angle])
    translate([x, y, 0])
    for (i = [0:cols-1])
        for (j = [0:rows-1])
            translate([i*pin_spacing, j*pin_spacing, pin_height/2])
                cylinder(h=pin_height, d=pin_diameter, center=true);
}

module atmega328() {
    color("black")
    translate([5, 0, board_thickness/2 + ic_height/2])
    cube([ic_length, ic_width, ic_height], center=true);

    // IC legs
    color("silver")
    for (side = [0:3])
        rotate([0, 0, side*90])
        translate([ic_length/2 - 0.5, 0, board_thickness/2 + 0.5])
        for (i = [-3:4])
            translate([0, i*1.27, 0])
                cube([1, 0.5, 1]);
}

module usb_connector() {
    color("silver")
    translate([board_width/2 - 3, 0, board_thickness/2 + usb_height/2])
        cube([usb_depth, usb_width, usb_height], center=true);

    // USB shield
    color("silver")
    translate([board_width/2 - 3, 0, board_thickness/2 + usb_height + 1])
        cube([usb_depth + 2, usb_width + 2, 0.5], center=true);
}

module power_jack() {
    color("black")
    translate([-board_width/2 + 5, 0, board_thickness/2 + power_jack_h/2])
        cylinder(h=power_jack_h, d=power_jack_d, center=true);

    // Center pin
    color("silver")
    translate([-board_width/2 + 5, 0, board_thickness/2 + power_jack_h/2])
        cylinder(h=power_jack_h + 1, d=2, center=true);
}

module capacitor(x, y, height) {
    color("blue")
    translate([x, y, board_thickness/2 + height/2])
        cylinder(h=height, d=4, center=true);

    // Leads
    color("silver")
    translate([x - 1.5, y, board_thickness/2 + 1])
        cube([3, 0.5, height - 2], center=true);
}

module led(x, y, color_name) {
    color(color_name)
    translate([x, y, board_thickness/2 + 2])
        cylinder(h=2, d=3, center=true);

    // LED body
    color("clear")
    translate([x, y, board_thickness/2 + 3.5])
        sphere(d=3);
}

module reset_button() {
    color("red")
    translate([board_width/2 - 15, board_height/2 - 10, board_thickness/2 + 2])
        cube([8, 6, 3], center=true);
}

// Assembly
union() {
    board();

    // Main IC
    atmega328();

    // I/O Headers
    pin_header(-board_width/2 + 5, board_height/2 - 5, 6, 2, 270);
    pin_header(-board_width/2 + 5, -board_height/2 + 5, 6, 2, 270);
    pin_header(board_width/2 - 20, board_height/2 - 5, 8, 2, 270);
    pin_header(board_width/2 - 20, -board_height/2 + 5, 8, 2, 270);

    // Power headers
    pin_header(-board_width/2 + 5, 0, 4, 2, 270);

    // USB
    usb_connector();

    // Power jack
    power_jack();

    // Reset button
    reset_button();

    // LEDs
    led(board_width/2 - 30, board_height/2 - 5, "green");
    led(board_width/2 - 35, board_height/2 - 5, "yellow");
    led(board_width/2 - 40, board_height/2 - 5, "red");

    // Capacitors
    capacitor(-10, 10, 6);
    capacitor(-10, -10, 6);
    capacitor(20, 15, 8);
    capacitor(20, -15, 8);

    // Crystal oscillator
    color("silver")
    translate([-5, 5, board_thickness/2 + 2])
        cube([6, 2, 4], center=true);
}

