    color(color_pcb)
    cube([45, 35, 1.6], center=true);
}

// Module for main FPGA chip
module fpga_chip() {
    translate([0, 0, 1.6]) {
        // Chip body
        color(color_case)
        cube([25, 25, 3], center=true);

        // Top markings
        translate([0, 0, 1.51])
        color([0.8, 0.8, 0.8])
        cube([24, 24, 0.1], center=true);

        // Pins on all sides
        for(side = [0:3]) {
            rotate([0, 0, side * 90])
            translate([12.5, 0, 0])
            for(i = [-6:6]) {
                translate([0, i * 2, 0])
                color(color_gold)
                cube([1, 1, 0.5], center=true);
            }
        }
    }
}

// Module for decoupling capacitors
module capacitors() {
    for(pos = [[-15, -10], [-15, 10], [15, -10], [15, 10]]) {
        translate([pos[0], pos[1], 1.6])
        color([0.1, 0.1, 0.1])
        cylinder(h=2, d=1.5, center=true);
    }
}

// Module for configuration flash
module flash_chip() {
    translate([0, -12, 1.6])
    color(color_plastic)
    cube([8, 6, 2], center=true);
}

// Module for voltage regulators
module voltage_regulators() {
    for(pos = [[-20, 12], [20, 12]]) {
        translate([pos[0], pos[1], 1.6])
        color(color_metal)
        cube([5, 5, 3], center=true);
    }
}

// Module for JTAG header
module jtag_header() {
    translate([-22, 0, 1.6])
    color(color_plastic)
    cube([6, 12, 5], center=true);

    // Pin holes
    for(i = [-4:4:4]) {
        translate([-22, i, 3])
        color([0, 0, 0])
        cylinder(h=5, d=1, center=true);
    }
}

// Module for GPIO headers
module gpio_headers() {
    // Left header
    translate([-22, 0, 5])
    rotate([90, 0, 0])
    color(color_plastic)
    cube([12, 2, 2], center=true);

    // Right header
    translate([22, 0, 5])
    rotate([90, 0, 0])
    color(color_plastic)
    cube([12, 2, 2], center=true);

    // Top header
    translate([0, -17, 5])
    rotate([90, 90, 0])
    color(color_plastic)
    cube([12, 2, 2], center=true);

    // Bottom header
    translate([0, 17, 5])
    rotate([90, 90, 0])
    color(color_plastic)
    cube([12, 2, 2], center=true);
}

// Module for status LEDs
module status_leds() {
    for(i = [0:3]) {
        translate([10 - i*5, 15, 1.6])
        color(color_led)
        cylinder(h=0.5, d=2, center=true);
    }
}

// Module for oscillator
module oscillator() {
    translate([10, -12, 1.6])
    color(color_metal)
    cube([4, 4, 2], center=true);
}

// Module for reset button
