// Colors
PCB_GREEN   = [0.1, 0.4, 0.1];
BLACK_RESIN = [0.05, 0.05, 0.05];
SILVER_PIN  = [0.7, 0.7, 0.7];
GOLD_PAD    = [0.8, 0.7, 0.2];
WHITE_LABEL = [0.9, 0.9, 0.9];

$fn = 32; // smooth curves

// ---------- PCB substrate ----------
module pcb_base() {
    color(PCB_GREEN) {
        difference() {
            cube([55, 55, 1.6], center = true);
            translate([0, 0, 0.8]) // chamfer edges for printability
                cube([55+0.2, 55+0.2, 1.8], center = true);
        }
    }
}

// ---------- Through-hole plated pads ----------
module pads() {
    for (p = [[-22,-22], [-22, 0], [-22, 22], [0, -22], [0, 22], [22, -22], [22, 0], [22, 22]])
        translate([p.x, p.y, 0.8]) color(GOLD_PAD)
            cylinder(h = 0.8, d = 3.2);
}

// ---------- 64-pin QFP package ----------
module qfp64() {
    color(BLACK_RESIN) {
        translate([0, 0, 1.6 + 0.5]) cube([14, 14, 2], center = true);
    }

    // 64 silver leads (16 per side, 0.5 mm thick, 0.4 mm pitch)
    for (side = [0:1:3]) {
        rotate([0, 0, side * 90])
            for (i = [-7.5:1:7.5])
                translate([8.5, i * 0.8, 1.6 + 0.5 + 1]) color(SILVER_PIN)
                    cube([1.5, 0.3, 0.5]);
    }
}

// ---------- Silkscreen label ----------
module label() {
    translate([0, -10, 1.6 + 0.05])
        color(WHITE_LABEL)
            linear_extrude(height = 0.1)
                text("QFP64", size = 2.5, halign = "center");
}

// ---------- Decoupling capacitors ----------
module caps() {
    for (pos = [[-10, 15], [10, 15], [-10, -15], [10, -15]]) {
        translate([pos.x, pos.y, 1.6 + 0.6]) color(BLACK_RESIN)
            cube([2.5, 1.2, 1.2]);
        translate([pos.x, pos.y, 1.6])
            color(SILVER_PIN)
                cylinder(h = 0.6, d = 0.8);
    }
}

// ---------- JTAG connector ----------
module jtag_header() {
    translate([27, 0, 1.6 + 0.5]) difference() {
        color(BLACK_RESIN) cube([8, 5, 2.5]);
        for (i = [-1.5:1:1.5])
            translate([0, i * 1.25, 1.25])
                cylinder(h = 2.5, d = 0.6);
    }
}

// ---------- Top silkscreen layer ----------
module silkscreen() {
    linear_extrude(height = 0.05)
        translate([0, 20, 1.6 + 0.05])
            color(WHITE_LABEL)
                text("CHIPSET v1.2", size = 2, halign = "center");
}

// ---------- Assembly ----------
union() {
    pcb_base();
    pads();
    qfp64();
    label();
    caps();
    jtag_header();
    silkscreen();
}

