// Rotary Encoder Module
// Medium complexity realistic encoder with shaft, body, mounting bracket and connector

$fn = 50;

// Main dimensions
body_d = 40;
body_h = 30;
flange_d = 50;
flange_h = 3;
shaft_d = 6;
shaft_l = 20;
mount_w = 58;
mount_h = 15;
mount_t = 3;
pcb_w = 36;
pcb_h = 20;
pcb_t = 1.6;
connector_w = 12;
connector_h = 8;
connector_l = 15;

// Colors
body_color = [0.2, 0.2, 0.2];
shaft_color = [0.8, 0.8, 0.8];
flange_color = [0.15, 0.15, 0.15];
mount_color = [0.5, 0.5, 0.5];
pcb_color = [0.0, 0.4, 0.0];
connector_color = [0.1, 0.1, 0.1];
led_color = [1, 0, 0];

module encoder_body() {
    // Main cylindrical body
    color(body_color)
    cylinder(d=body_d, h=body_h);

    // Top flange with mounting holes
    color(flange_color)
    translate([0, 0, body_h])
    cylinder(d=flange_d, h=flange_h);

    // Mounting holes (4x M3)
    for(i=[0:3]) {
        rotate([0, 0, i*90+45])
        translate([flange_d/2*cos(45)-1.5, flange_d/2*sin(45)-1.5, body_h])
        color(flange_color)
        cylinder(d=3.2, h=flange_h+0.1);
    }
}

module encoder_shaft() {
    // Main shaft
    color(shaft_color)
    translate([0, 0, body_h+flange_h])
    cylinder(d=shaft_d, h=shaft_l);

    // Flat on shaft
    color(shaft_color)
    translate([0, -shaft_d/2, body_h+flange_h+shaft_l/2])
    cube([shaft_d/2, shaft_d, shaft_l], center=true);

    // Shaft collar
    color(body_color)
    translate([0, 0, body_h+flange_h+1])
    cylinder(d=shaft_d+4, h=2);
}

module mounting_bracket() {
    // Main bracket
    color(mount_color)
    translate([0, -body_d/2-mount_h/2, -mount_t])
    cube([mount_w, mount_h, mount_t], center=true);

    // Mounting slots
    for(i=[-1:1]) {
        translate([i*15, -body_d/2-mount_h/2, -mount_t])
        color([0,0,0])
        cube([4, mount_h+0.1, mount_t+0.2], center=true);
    }
}

module pcb_assembly() {
    // PCB
    color(pcb_color)
    translate([0, body_d/2+pcb_h/2, body_h/2-pcb_t/2])
    cube([pcb_w, pcb_h, pcb_t], center=true);

    // Connector
    color(connector_color)
    translate([0, body_d/2+pcb_h+connector_l/2, body_h/2-connector_h/2])
    cube([connector_w, connector_l, connector_h], center=true);

    // Connector pins
    for(i=[-1.5:1.5]) {
        color([0.8,0.8,0.8])
        translate([i*2.54, body_d/2+pcb_h+connector_l+1, body_h/2-connector_h/2+2])
        cylinder(d=0.6, h=5);
    }

    // Indicator LED
    color(led_color)
    translate([10, body_d/2+pcb_h/2, body_h/2+pcb_t+1])
    sphere(d=3);
}

module labels() {
    // Encoder label on body
    color([1,1,1])
    translate([0, body_d/2+0.1, body_h/2])
    rotate([0, 90, 0])
    linear_extrude(height=0.1)
    text("ENCODER", size=4, halign="center", valign="center");

    // Arrow direction indicator
    color([1,1,0])
    translate([0, body_d/2+0.2, body_h/2+8])
    rotate([0, 90, 0])
    linear_extrude(height=0.1)
    text("â†’", size=6, halign="center", valign="center");
}

// Assembly
union() {
    encoder_body();
    encoder_shaft();
    mounting_bracket();
    pcb_assembly();
    labels();
}

