// ====== MAIN MODULE ======
module multiplexer_74HC4051() {
    difference() {
        // Main plastic body
        color(body_color)
        union() {
            // Body with rounded top
            translate([0, 0, body_h/2])
                minkowski() {
                    cube([body_l, body_w, body_h*0.8], center=true);
                    sphere(0.8);
                }

            // Pin support rails
            translate([0, (body_w+pin_pitch*2)/2-pin_pitch, 0])
                cube([body_l+2, pin_pitch, body_h*0.3], center=true);
            translate([0, -(body_w+pin_pitch*2)/2+pin_pitch, 0])
                cube([body_l+2, pin_pitch, body_h*0.3], center=true);

            // Identification notch
            translate([body_l/2-1, 0, body_h-0.5])
                rotate([90, 0, 0])
                    cylinder(r=0.8, h=0.8, $fn=20);
        }

        // Pin holes (for 3D printing support)
        for (i = [0:7]) {
            translate([-body_l/2-0.5, -body_w/2+pin_pitch + i*pin_pitch, body_h/2-1])
                rotate([0, 90, 0])
                    cylinder(r=pin_w*0.8, h=3, $fn=8);
        }
        for (i = [0:7]) {
            translate([-body_l/2-0.5, body_w/2-pin_pitch - i*pin_pitch, body_h/2-1])
                rotate([0, 90, 0])
                    cylinder(r=pin_w*0.8, h=3, $fn=8);
        }
    }

    // Pins
    color(pin_color)
    for (i = [0:7]) {
        // Left side pins 1-8
        translate([-body_l/2, -body_w/2+pin_pitch + i*pin_pitch, body_h/2-1])
            rotate([90, 0, 0])
                cube([pin_l, pin_w, pin_h], center=true);

        // Right side pins 16-9
        translate([-body_l/2, body_w/2-pin_pitch - i*pin_pitch, body_h/2-1])
            rotate([90, 0, 0])
                cube([pin_l, pin_w, pin_h], center=true);
    }

    // Pin 1 indicator dot
    color(led_color)
    translate([body_l/2-1.5, -body_w/2+pin_pitch, body_h-0.5])
        sphere(r=0.4);

    // Package markings
    color(text_color)
    translate([0, 0, body_h-0.1])
        linear_extrude(height=0.1)
            text("74HC4051", size=1.5, halign="center", valign="center");

    // Enable indicator LED
    color(led_color)
    translate([body_l/2-2.5, 0, body_h-0.8])
        cylinder(d=led_d, h=led_h, $fn=12);
}

