                cube([case_length-6, case_width-6, case_thickness-2], center=true);
            translate([0, 0, 2])
                scale([0.9, 0.9, 1])
                cube([case_length-6, case_width-6, case_thickness-2], center=true);
        }

        // Display window recess
        translate([10, 0, case_thickness/2-1])
            cube([35, 30, 2], center=true);

        // Speaker grill holes
        for(x = [-30:-20]) {
            for(y = [-20:5:20]) {
                translate([x, y, case_thickness/2-0.5])
                    cylinder(h=2, d=2, center=true);
            }
        }
    }
}

// Display screen
module display() {
    color("black")
    translate([10, 0, case_thickness/2-0.5])
        cube([33, 27, 0.5], center=true);

    // LCD segments
    color("lime")
    for(i = [-10:6:10]) {
        translate([i, 0, case_thickness/2])
            cube([4, 20, 0.2], center=true);
    }
}

// Buttons and controls
module controls() {
