    translate([-25, 0, case_thickness/2-0.5])
        cylinder(h=2, d=6);

    // Calibration button (blue)
    color("blue")
    translate([-25, 20, case_thickness/2-0.5])
        cylinder(h=2, d=6);

    // Rotary dial
    color("gray")
    translate([-35, 0, case_thickness/2-2])
        cylinder(h=5, d=15);
    color("black")
    translate([-35, 0, case_thickness/2])
        cube([2, 10, 1], center=true);
}

// Probe connector BNC
module bnc_connector() {
    color("silver")
    translate([case_length/2-5, 0, 0])
        rotate([90, 0, 0])
        difference() {
            union() {
                cylinder(h=15, d=12);
                translate([0, 0, 15])
                    cylinder(h=5, d=8);
            }
            cylinder(h=20, d=6);
        }
