// 75-key compact mechanical keyboard (75 %)
// Overall footprint: 95 mm Ã— 32 mm Ã— 6 mm
// 3D-printable, no support, 1 mm wall thickness

// ---------- color constants ----------
$fn = 32;               // smooth curves
wall = 1;               // printable wall thickness
key_spacing = 12;       // 12 mm center-to-center
keycap_h = 5;
case_h = 6;

// ---------- main body ----------
module case() {
    difference() {
        color("silver")
            cube([95, 32, case_h], center = false);

        // hollow cavity for electronics
        translate([wall, wall, wall])
            cube([95 - 2*wall, 32 - 2*wall, case_h - wall], center = false);
    }
}

// ---------- single MX-style keycap ----------
module keycap(row = 0) {
    // staggered profile per row (SA-like)
    profile = [5.5, 5.2, 5, 4.8, 4.6];

    translate([0, 0, profile[row]])
    difference() {
        // sculpted top
        scale([1, 1, 0.4])
            sphere(d = 10.5);

        // slightly rounded edges
        translate([0, 0, -0.5])
            scale([0.9, 0.9, 0.5])
                cube([10, 10, 6], center = true);

        // stem hole
        color("black")
            cylinder(h = 4, d = 4.2, center = false);
    }
}

// ---------- key matrix ----------
module key_matrix() {
    // 75-key layout (15 Ã— 5)
    rows = 5;
    cols = 15;

    for (r = [0:rows-1]) {
        for (c = [0:cols-1]) {
            // skip empty positions
            if (!(r == 0 && c > 12) && !(r == 1 && c > 14)) {
                translate([c * key_spacing + 2, 
                          (rows - r - 1) * key_spacing + 2, 
                          case_h])
                    color("ivory")
                        keycap(r);
            }
        }
    }
}

// ---------- PCB with visible traces ----------
module pcb() {
    translate([wall, wall, case_h - 1.6])
    color("darkgreen")
    cube([95 - 2*wall, 32 - 2*wall, 1.6]);
}

// ---------- USB-C port ----------
module usbc_port() {
    translate([95 - 7, 16 - 4, 2])
    difference() {
        color("black")
            cube([7, 8, 4]);
        translate([1, 2, 1])
            cube([5, 4, 2]);
    }
}

// ---------- status LEDs ----------
module status_leds() {
    for (i = [0:2]) {
        translate([2 + i*4, 2, case_h - 0.2])
            color("red")
                sphere(d = 1.5);
    }
}

// ---------- logo engraving ----------
module logo() {
    translate([95 - 15, 32 - 8, 0.2])
    linear_extrude(height = 0.5)
        text("MK-V3", size = 5);
}

// ---------- assembly ----------
union() {
    case();
    pcb();
    key_matrix();
    usbc_port();
    status_leds();
    logo();
}

