// Module for rounded cube edges
module rounded_cube(size, radius) {
    x = size[0];
    y = size[1];
    z = size[2];

    hull() {
        translate([radius, radius, radius]) sphere(r=radius);
        translate([x-radius, radius, radius]) sphere(r=radius);
        translate([radius, y-radius, radius]) sphere(r=radius);
        translate([x-radius, y-radius, radius]) sphere(r=radius);
        translate([radius, radius, z-radius]) sphere(r=radius);
        translate([x-radius, radius, z-radius]) sphere(r=radius);
        translate([radius, y-radius, z-radius]) sphere(r=radius);
        translate([x-radius, y-radius, z-radius]) sphere(r=radius);
    }
}

// Main monitor body
module monitor_body() {
    difference() {
        // Outer shell
        color([0.2, 0.2, 0.2]) rounded_cube([width, height, depth], 2);

        // Screen recess
        translate([screen_bezel, screen_bezel, -1])
            cube([screen_width, screen_height, depth + 2]);

        // Ventilation slots on back
