module rounded_cube(size, radius) {
    // Create a rounded cube using sphere and cube differences
    difference() {
        cube(size, center = true);
        translate([size[0]/2 - radius, size[1]/2 - radius, size[2]/2 - radius])
            sphere(r = radius);
        translate([-size[0]/2 + radius, size[1]/2 - radius, size[2]/2 - radius])
            sphere(r = radius);
        translate([size[0]/2 - radius, -size[1]/2 + radius, size[2]/2 - radius])
            sphere(r = radius);
        translate([-size[0]/2 + radius, -size[1]/2 + radius, size[2]/2 - radius])
            sphere(r = radius);
        translate([size[0]/2 - radius, size[1]/2 - radius, -size[2]/2 + radius])
            sphere(r = radius);
        translate([-size[0]/2 + radius, size[1]/2 - radius, -size[2]/2 + radius])
            sphere(r = radius);
        translate([size[0]/2 - radius, -size[1]/2 + radius, -size[2]/2 + radius])
            sphere(r = radius);
        translate([-size[0]/2 + radius, -size[1]/2 + radius, -size[2]/2 + radius])
            sphere(r = radius);
    }
}

