    }

    // Keyboard base half
    translate([0,-55,0]) rotate([180,0,0]) {
        difference() {
            color(colorBody) cube([80,55,3], center=true);

            // Beveled edges
            translate([0,0,-0.5]) scale([0.9,0.9,1]) cube([80,55,3], center=true);

