// Realistic Phone Model - 3D Printable
// Dimensions in mm, scaled for 3D printing (1:1 scale)

// Main color scheme
screen_color = [0, 0, 0];
body_color = [0.2, 0.2, 0.2];
button_color = [0.1, 0.1, 0.1];
camera_color = [0.05, 0.05, 0.05];
speaker_color = [0.15, 0.15, 0.15];

// Overall dimensions
phone_length = 140;
phone_width = 68;
phone_thickness = 7.8;
wall_thickness = 1.2;

// Screen dimensions
screen_length = 125;
screen_width = 60;
screen_depth = 0.5;

// Camera module
camera_diameter = 12;
camera_height = 1.5;
camera_offset_x = -phone_length/2 + 25;
camera_offset_y = phone_width/2 - 15;

// Flash LED
flash_diameter = 4;
flash_height = 0.8;
flash_offset_x = camera_offset_x + 20;

// Buttons
button_width = 3;
button_height = 12;
button_depth = 1;

// Speaker grill holes
grill_holes = 12;
grill_hole_diameter = 1;
grill_length = 30;
grill_width = 4;

// USB-C port
usb_width = 8;
usb_height = 2.4;
usb_depth = 3;

// Main phone body
module phone_body() {
    difference() {
        // Main body with rounded edges
        union() {
            cube([phone_length, phone_width, phone_thickness], center=true);
            // Rounded corners using spheres
            translate([phone_length/2-wall_thickness, phone_width/2-wall_thickness, 0])
                sphere(r=wall_thickness);
            translate([-phone_length/2+wall_thickness, phone_width/2-wall_thickness, 0])
                sphere(r=wall_thickness);
            translate([phone_length/2-wall_thickness, -phone_width/2+wall_thickness, 0])
                sphere(r=wall_thickness);
            translate([-phone_length/2+wall_thickness, -phone_width/2+wall_thickness, 0])
                sphere(r=wall_thickness);
        }

        // Screen cutout
        translate([0, 0, phone_thickness/2 - screen_depth/2])
            cube([screen_length, screen_width, screen_depth + 0.1], center=true);

        // Camera hole
        translate([camera_offset_x, camera_offset_y, phone_thickness/2])
            cylinder(h=camera_height + 0.2, r=camera_diameter/2, center=true);

        // Flash hole
        translate([flash_offset_x, camera_offset_y, phone_thickness/2])
            cylinder(h=flash_height + 0.2, r=flash_diameter/2, center=true);
    }
}

// Screen assembly
module phone_screen() {
    color(screen_color)
    translate([0, 0, phone_thickness/2 - screen_depth/2])
        cube([screen_length, screen_width, screen_depth], center=true);
}

// Camera module
module camera_module() {
    color(camera_color)
    translate([camera_offset_x, camera_offset_y, phone_thickness/2 - camera_height/2])
        cylinder(h=camera_height, r=camera_diameter/2, center=true);

    // Camera lens detail
    color([0, 0, 0])
    translate([camera_offset_x, camera_offset_y, phone_thickness/2 - camera_height/2])
        cylinder(h=camera_height + 0.1, r=camera_diameter/3, center=true);
}

// Flash LED
module flash_led() {
    color([1, 1, 0.8])
    translate([flash_offset_x, camera_offset_y, phone_thickness/2 - flash_height/2])
        cylinder(h=flash_height, r=flash_diameter/2, center=true);
}

// Side buttons
module side_buttons() {
    color(button_color)
    // Volume buttons
    translate([phone_length/2 - button_width/2, 0, phone_thickness/4])
        cube([button_width, button_height, button_depth], center=true);
    translate([phone_length/2 - button_width/2, -phone_width/4, phone_thickness/4])
        cube([button_width, button_height, button_depth], center=true);

    // Power button
    translate([-phone_length/2 + button_width/2, 0, phone_thickness/4])
        cube([button_width, button_height * 1.5, button_depth], center=true);
}

// Speaker grill
module speaker_grill() {
    color(speaker_color)
    translate([0, phone_width/2 - grill_width/2 - 2, -phone_thickness/2 + grill_width/2])
        cube([grill_length, grill_width, wall_thickness], center=true);

    // Grill holes
    for(i = [0:grill_holes-1]) {
        translate([-grill_length/2 + (i+0.5)*(grill_length/grill_holes), 
                   phone_width/2 - 2, -phone_thickness/2 - 0.1])
            cylinder(h=wall_thickness + 0.2, r=grill_hole_diameter/2, center=true);
    }
}

// USB-C port
module usb_port() {
    color(button_color)
    translate([0, -phone_width/2 + usb_width/2 + 2, -phone_thickness/2 + usb_height/2])
        cube([usb_width, usb_depth, usb_height], center=true);
}

// Main assembly
color(body_color) phone_body();
color(screen_color) phone_screen();
color(camera_color) camera_module();
flash_led();
color(button_color) side_buttons();
speaker_grill();
usb_port();

// OpenSCAD code for a realistic 1990s-style pager
// Medium complexity, 3D printable, no overhangs

// Overall dimensions (mm)
length = 75;
width = 45;
height = 18;

// Wall thickness for 3D printing
wall = 1.5;

// Main body module
module pager_body() {
    difference() {
        // Outer rounded shell
        union() {
            rounded_box(length, width, height, 3);
        }

        // Hollow out interior
        translate([0,0,wall])
            rounded_box(length-wall*2, width-wall*2, height-wall, 2.5);

        // Screen cutout
        translate([0, 12, height])
            cube([35, 12, 2], center=true);

        // Button cutouts
        for (i = [0:2]) {
            translate([-20 + i*20, -12, height])
                cylinder(h=2, r=4, center=true);
        }
    }
}

// Rounded box helper module
module rounded_box(l, w, h, r) {
    hull() {
        for (x = [-1, 1])
            for (y = [-1, 1])
                for (z = [-1, 1])
                    translate([x*(l/2-r), y*(w/2-r), z*(h/2-r)])
                        sphere(r=r);
    }
}

// LCD screen module
module lcd_screen() {
    color([0.2, 0.2, 0.2])
    translate([0, 12, height-0.1])
        cube([33, 10, 0.5], center=true);

    // LCD display area
    color([0.1, 0.8, 0.1])
    translate([0, 12.5, height-0.05])
        cube([30, 7, 0.3], center=true);
}

// Button module
module buttons() {
    for (i = [0:2]) {
        // Button
        color([0.1, 0.1, 0.1])
        translate([-20 + i*20, -12, height-0.1])
            cylinder(h=1.5, r=3.5, center=true);

        // Button top
        color([0.8, 0.8, 0.8])
        translate([-20 + i*20, -12, height+0.1])
            cylinder(h=0.5, r=3, center=true);
    }
}

// Side button
module side_button() {
    color([0.3, 0.3, 0.3])
    translate([length/2+1, 0, height/2])
        rotate([0, 90, 0])
            cylinder(h=8, r=2, center=true);
}

// Antenna stub
module antenna() {
    color([0.1, 0.1, 0.1])
    translate([0, width/2+3, height-5])
        cylinder(h=8, r=1.5);
}

// Belt clip
module belt_clip() {
    color([0.5, 0.5, 0.5])
    difference() {
        translate([0, -width/2-2, height/2])
            cube([40, 4, 12], center=true);

        // Clip opening
        translate([0, -width/2-4, height/2-2])
            cube([30, 4, 8], center=true);
    }
}

// LED indicator
module led_indicator() {
    color([0.8, 0, 0])
    translate([length/2-8, width/2-3, height-0.1])
        cylinder(h=0.5, r=1.5);
}

// Speaker grill
module speaker_grill() {
    color([0.2, 0.2, 0.2])
    translate([0, -18, height-0.1])
        cube([25, 8, 0.5], center=true);

    // Grill holes
    for (x = [-10:5:10])
        for (y = [-3:2:3]) {
            translate([x, y-18, height+0.1])
                cylinder(h=1, r=0.5, center=true);
        }
}

// Battery cover
module battery_cover() {
    color([0.3, 0.3, 0.3])
    translate([0, 0, 0])
        cube([length-4, width-4, 1], center=true);
}

