// Walkie-Talkie model for 3D printing (realistic style, medium complexity)
// Overall dimensions ~90 mm tall

// Color definitions
color_case = [0.15, 0.15, 0.15];    // Dark gray
color_body = [0.25, 0.25, 0.25];    // Medium gray
color_screen = [0.05, 0.05, 0.05];  // Black
color_button = [0.1, 0.1, 0.1];     // Dark
color_antenna = [0.4, 0.4, 0.4];    // Light gray

// Main module
module walkie() {
    difference() {
        union() {
            // Main body shell
            color(color_case)
            rounded_cube([40, 25, 85], 3);

            // Speaker grill area
            color(color_body)
            translate([0, 13, 30])
                cube([38, 1, 20], center = true);

            // Channel knob
            color(color_case)
            translate([20, 0, 65])
                rotate([90, 0, 0])
                    cylinder(h = 5, r = 6, $fn = 18);

            // Volume knob
            color(color_case)
            translate([-20, 0, 65])
                rotate([90, 0, 0])
                    cylinder(h = 5, r = 6, $fn = 18);

            // PTT button
            color(color_button)
            translate([0, -13, 45])
                rounded_cube([30, 8, 12], 2);

            // Antenna base
            color(color_case)
            translate([0, 13, 85])
                cylinder(h = 8, r1 = 8, r2 = 6, $fn = 20);

            // Antenna
            color(color_antenna)
            translate([0, 13, 93])
                cylinder(h = 40, r = 2, $fn = 16);

            // Clip mount
            color(color_case)
            translate([0, -13, 60])
                cube([3, 10, 25], center = true);
        }

        // Screen cutout
        color(color_screen)
        translate([0, 12.5, 55])
            cube([30, 0.5, 15], center = true);

        // Speaker holes
        for (i = [-2:2]) {
            translate([i * 7, 13.5, 30])
                cube([3, 1, 2], center = true);
        }

        // Microphone holes
        for (i = [-1:1]) {
            translate([i * 5, 13.5, 10])
                cube([2, 1, 2], center = true);
        }

        // LED indicator hole
        translate([15, 12.5, 55])
            cylinder(h = 2, r = 1.5, $fn = 8);

        // Charging port
        translate([0, 13, 5])
            cube([8, 1, 4], center = true);
    }

    // Screen detail
    color([0, 0.8, 0])
    translate([0, 12.4, 55])
        cube([28, 0.1, 13], center = true);

    // Buttons detail
    color(color_button)
    for (j = [0:2]) {
        translate([-10 + j * 10, -13.5, 55])
            rounded_cube([6, 1, 3], 1);
    }
}

// Helper module for rounded cubes
module rounded_cube(size, radius) {
    x = size[0]; y = size[1]; z = size[2];
    hull() {
        translate([radius - x/2, radius - y/2, radius - z/2])
            sphere(r = radius, $fn = 16);
        translate([x/2 - radius, radius - y/2, radius - z/2])
            sphere(r = radius, $fn = 16);
        translate([radius - x/2, y/2 - radius, radius - z/2])
            sphere(r = radius, $fn = 16);
        translate([x/2 - radius, y/2 - radius, radius - z/2])
            sphere(r = radius, $fn = 16);
        translate([radius - x/2, radius - y/2, z/2 - radius])
            sphere(r = radius, $fn = 16);
        translate([x/2 - radius, radius - y/2, z/2 - radius])
            sphere(r = radius, $fn = 16);
        translate([radius - x/2, y/2 - radius, z/2 - radius])
            sphere(r = radius, $fn = 16);
        translate([x/2 - radius, y/2 - radius, z/2 - radius])
            sphere(r = radius, $fn = 16);
    }
}

// Render the walkie
walkie();

