        color([0.2, 0.2, 0.2])
        minkowski() {
            cube([body_w, body_h, body_d], center=true);
            sphere(2);
        }

        // Hollow center
        color([0.15, 0.15, 0.15])
        minkowski() {
            cube([body_w-4, body_h-4, body_d-2], center=true);
            sphere(1);
        }
    }

    // Screen recess
    translate([0, 15, body_d/2-0.1])
    color([0.05, 0.05, 0.05])
    cube([screen_w, screen_h, screen_d], center=true);

    // Glass screen
    translate([0, 15, body_d/2+0.9])
    color([0.3, 0.3, 0.5, 0.7])
    cube([screen_w-2, screen_h-2, 0.5], center=true);

    // Speaker grill
    translate([0, 30, body_d/2-0.1])
    color([0.1, 0.1, 0.1])
    difference() {
        cube([35, 8, 1], center=true);
        for(i = [-15:3:15]) {
            translate([i, 0, 0])
            cylinder(h=2, d=1, center=true);
        }
    }

    // Microphone grill
    translate([0, -30, body_d/2-0.1])
    color([0.1, 0.1, 0.1])
    difference() {
        cube([20, 4, 1], center=true);
        for(i = [-8:2:8]) {
            translate([i, 0, 0])
            cylinder(h=2, d=0.8, center=true);
        }
    }

    // Button array
    for(x = [0:buttons_x-1]) {
        for(y = [0:buttons_y-1]) {
            translate([
                -((buttons_x-1)*button_spacing)/2 + x*button_spacing,
                -5 + y*button_spacing,
                body_d/2
            ]) {
                // Button base
                color([0.1, 0.1, 0.1])
                cylinder(h=1, d=button_size, center=true);

                // Button top
                color([0.3, 0.3, 0.3])
                translate([0, 0, 0.6])
                cylinder(h=0.5, d=button_size-1, center=true);
            }
        }
    }

    // Antenna nub
    translate([0, body_h/2-5, 0])
    color([0.4, 0.4, 0.4])
    cylinder(h=5, d=4, center=true);

    // Power button
    translate([-body_w/2-1, 0, 0])
    color([0.2, 0.2, 0.2])
    cube([2, 8, 3], center=true);

    // Volume buttons
    translate([body_w/2+1, 10, 0])
    color([0.2, 0.2, 0.2])
    cube([2, 8, 3], center=true);

    translate([body_w/2+1, -10, 0])
    color([0.2, 0.2, 0.2])
    cube([2, 8, 3], center=true);

    // Charging port
    translate([0, -body_h/2+5, -body_d/2])
    color([0.1, 0.1, 0.1])
    cube([8, 4, 2], center=true);

    // LED indicator
    translate([screen_w/2-3, 15+screen_h/2+2, body_d/2-0.1])
    color([0, 1, 0, 0.8])
    cylinder(h=0.5, d=2, center=true);
}

