// Satellite Electronic Device - CubeSat Style
// Medium complexity 3D printable model with realistic features

$fn = 32;

// Main dimensions (mm)
main_body = [50, 50, 30];
solar_panel = [25, 50, 2];
antenna_length = 20;
wall_thickness = 2;

// Colors
color_body = [0.8, 0.8, 0.9];      // Light gray aluminum
color_solar = [0.1, 0.1, 0.4];     // Dark blue solar cells
color_gold = [0.8, 0.6, 0.2];      // Gold contacts
color_black = [0.1, 0.1, 0.1];     // Black components

// Main satellite body
module satellite_body() {
    color(color_body) {
        difference() {
            // Main cube structure
            cube(main_body, center=true);

            // Hollow interior
            translate([0, 0, 0])
                cube([main_body.x-wall_thickness*2, main_body.y-wall_thickness*2, main_body.z-wall_thickness], center=true);

            // Access ports on sides
            for(side = [-1, 1]) {
                translate([side * (main_body.x/2 - wall_thickness), 0, main_body.z/4])
                    rotate([0, 90, 0])
                        cylinder(h=wall_thickness*2, r=5, center=true);
            }
        }

        // Corner reinforcements
        for(x = [-1, 1])
        for(y = [-1, 1])
        for(z = [-1, 1]) {
            translate([x*main_body.x/2 - x*wall_thickness, y*main_body.y/2 - y*wall_thickness, z*main_body.z/2 - z*wall_thickness])
                cube([wall_thickness*2, wall_thickness*2, wall_thickness*2], center=true);
        }
    }
}

// Solar panel array
module solar_panels() {
    color(color_solar) {
        // Generate 4 solar panels
        for(side = [-1, 1]) {
            // Side panels
            translate([side * (main_body.x/2 + solar_panel.z/2), 0, 0])
                rotate([0, 90, 0])
                    cube(solar_panel, center=true);

            // Top/bottom panels
            translate([0, side * (main_body.y/2 + solar_panel.z/2), 0])
                rotate([90, 0, 0])
                    cube(solar_panel, center=true);
        }

        // Solar cell grid pattern
        color(color_black) {
            for(side = [-1, 1])
            for(i = [-20:10:20])
            for(j = [-20:10:20]) {
                translate([side * (main_body.x/2 + solar_panel.z), i, j])
                    cube([0.5, 8, 8], center=true);
            }
        }
    }

    // Gold contact strips
    color(color_gold) {
        for(side = [-1, 1]) {
            translate([side * (main_body.x/2 + solar_panel.z/2), 0, solar_panel.y/2 - 2])
                rotate([0, 90, 0])
                    cube([solar_panel.x, 1, solar_panel.z], center=true);
        }
    }
}

// Communication antennas
module antennas() {
    color(color_gold) {
        // Deployable dipole antennas
        for(angle = [0:120:359]) {
            rotate([0, 0, angle]) {
                translate([main_body.x/2 + 5, 0, main_body.z/2 - 5])
                    rotate([0, 90, 0])
                        cylinder(h=antenna_length, r=1, center=true);
            }
        }

        // Main communication dish
        translate([0, 0, main_body.z/2 + 10])
            rotate_extrude(angle=360)
                translate([8, 0, 0])
                    circle(r=1);
    }
}

// Electronic components visible through window
module electronics() {
    color([0.2, 0.2, 0.3]) {
        // Main circuit board
        translate([0, 0, -main_body.z/4])
            cube([40, 40, 2], center=true);

        // Processor chip
        color([0.3, 0.3, 0.3])
            translate([0, 0, -main_body.z/4 + 2])
                cube([10, 10, 1], center=true);

        // Memory modules
        for(pos = [[-15, 0], [15, 0], [0, -15], [0, 15]]) {
            translate([pos[0], pos[1], -main_body.z/4 + 2])
                cube([5, 5, 2], center=true);
        }

        // Status LED
