module dongle_body() {
    color(plastic_color) {
        difference() {
            // Outer shell
            cube([dongle_length, dongle_width, dongle_height], center = true);

            // Hollow interior
            translate([0, 0, -wall_thickness/2])
                cube([dongle_length - 2*wall_thickness, 
                      dongle_width - 2*wall_thickness, 
                      dongle_height - wall_thickness], center = true);

            // USB-C connector cutout
            translate([-dongle_length/2 - 1, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h = 10, r = 5.5, center = true);
        }
    }
}

// USB-C connector
module usb_c_connector() {
    color(metal_color) {
        translate([-dongle_length/2 - 5.5, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = 11, r = 6.5, center = true);

        // Inner plastic part
        color("Black") {
            translate([-dongle_length/2 - 5.5, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h = 8, r = 5.5, center = true);
        }
    }
}

// Port module
module port(type, position_x, position_y) {
    if (type == "HDMI") {
        // HDMI port
        color(port_color) {
            translate([position_x, position_y, dongle_height/2 - 1])
                cube([15, 12, 2], center = true);

            // Inner hole
            translate([position_x, position_y, dongle_height/2 + 0.1])
                cube([13, 10, 2], center = true);
        }

        // Screws
        for (i = [-1, 1]) {
            translate([position_x + i*6, position_y + 5, dongle_height/2 - 0.5])
                cylinder(h = 1, r = 0.5);
        }
    }

    if (type == "USB-A") {
        // USB-A port
        color(port_color) {
            translate([position_x, position_y, dongle_height/2 - 1])
                cube([12, 15, 2], center = true);

            // Inner hole
            translate([position_x, position_y, dongle_height/2 + 0.1])
                cube([11, 14, 2], center = true);
        }

        // Plastic tongue
        color("Black") {
            translate([position_x, position_y - 2, dongle_height/2 - 0.8])
                cube([9.5, 8, 1.5], center = true);
        }
    }

    if (type == "SD") {
        // SD card slot
        color(port_color) {
            translate([position_x, position_y, dongle_height/2 - 1])
                cube([20, 2, 3], center = true);

            // Beveled edge
            translate([position_x - 8, position_y, dongle_height/2])
                cube([4, 2, 1], center = true);
        }
    }
}

// Status LED
module status_led() {
    color(led_color) {
        translate([dongle_length/2 - 5, dongle_width/2 - 3, dongle_height/2 - 0.1])
            cylinder(h = 1, r = 1.5);
    }
}

// Ventilation holes
module ventilation_holes() {
    color(plastic_color) {
        for (i = [0:3]) {
            translate([-dongle_length/2 + 10 + i*10, 0, dongle_height/2 - 0.1])
                cube([6, dongle_width - 10, 1], center = true);
        }
    }
}

// Brand text (simulated as indentations)
module brand_text() {
    translate([dongle_length/4, dongle_width/4, dongle_height/2 - 0.5])
        cube([10, 2, 0.5], center = true);
    translate([dongle_length/4, -dongle_width/4, dongle_height/2 - 0.5])
        cube([10, 2, 0.5], center = true);
}

