// Datacard electronic device - realistic style, medium complexity
// A modern USB-C flash drive with activity LED and textured grip

// Main dimensions
length = 60;
width = 20;
height = 8;
wall = 1.2;

// Colors
color_plastic = [0.2, 0.2, 0.2];  // Dark gray
color_metal = [0.8, 0.8, 0.8];   // Silver
color_led = [0, 1, 0];          // Green LED
color_label = [0.95, 0.95, 0.95]; // Light gray label

module datacard() {
    // Main body
    color(color_plastic) {
        difference() {
            // Outer shell
            cube([length, width, height], center = true);

            // Hollow interior for weight reduction
            translate([0, 0, 0])
                cube([length - wall*2, width - wall*2, height - wall], center = true);

            // USB-C port opening
            translate([length/2 - 1, 0, 0])
                rotate([0, 90, 0])
                cylinder(h = 2.5, d1 = 8.4, d2 = 8.4, center = true);
        }
    }

    // USB-C connector
    color(color_metal) {
        translate([length/2 - 4, 0, 0])
        difference() {
            // Metal shell
            cube([8, 12, height - 2], center = true);

            // Inner cavity
            translate([0, 0, 1])
                cube([6, 10, height - 4], center = true);

            // Tongue
            translate([-1, 0, 0])
                cube([2, 7, 0.8], center = true);

            // Contact pads
            for (i = [0:5]) {
                translate([0, -3 + i*1.2, -1.6])
                    cube([1.5, 0.6, 0.1], center = true);
            }
        }
    }

    // Activity LED window
    color(color_led) {
        translate([-length/4, width/2 - 1, height/2 - 1])
            cube([3, 0.5, 0.5]);
    }

    // Textured grip pattern
    color(color_plastic) {
        for (x = [-20:5:15]) {
            for (y = [-7:3:7]) {
                translate([x, y, -height/2 + 0.1])
                    cube([4, 2, 0.2]);
            }
        }
    }

    // Keychain hole
    translate([-length/2 + 3, 0, 0])
    rotate([0, 90, 0])
    cylinder(h = width, d = 3, center = true);

    // Brand label area
    color(color_label) {
        translate([0, 0, height/2 - 0.1])
            cube([15, 8, 0.1], center = true);
    }

    // Capacity indicator dots
    color([0.9, 0.9, 0.9]) {
        for (i = [0:3]) {
            translate([length/2 - 15 - i*3, -width/2 + 2, height/2 - 0.5])
                sphere(d = 1);
        }
    }
}

