module router_body() {
    // Main plastic enclosure
    difference() {
        color([0.1,0.1,0.1]) rounded_box([75,50,15], 3);

        // Ventilation slots on top
        for(y=[-20:10:20]) {
            translate([-30, y, 7.5])
                cube([60, 1.5, 5]);
        }

        // LED indicators hole
        translate([32, -20, 7.5])
            cube([8, 2, 2]);
    }

    // Front panel with glossy finish
    color([0.05,0.05,0.05]) 
    translate([0, 0, 7.5])
        rounded_box([75.2,50.2,0.5], 3);

    // Status LEDs
    for(i=[0:2:8]) {
        color([0,1,0]) 
        translate([32-i, -20, 7.5])
            sphere(0.5);
    }

    // Brand logo area
    color([0.8,0.8,0.8])
    translate([20, 15, 7.5])
        cube([20, 5, 0.1]);

    // Antenna arrays (4 antennas)
    for(pos=[[-35,25,7.5], [35,25,7.5], [-35,-25,7.5], [35,-25,7.5]]) {
        translate(pos) {
            rotate([90,0,0]) {
                // Antenna base
                color([0.2,0.2,0.2])
                cylinder(h=2, r1=3, r2=3);

                // Antenna shaft
                color([0.15,0.15,0.15])
                translate([0,0,2])
                    cylinder(h=15, r1=1.5, r2=1.5);
            }
        }
    }

    // Ethernet ports (4x)
    for(i=[0:3]) {
        translate([-25+i*12, -22.5, -5]) {
            color([0.3,0.3,0.3])
            difference() {
                cube([10, 5, 5]);
                translate([2,2,2.5])
                    cube([6, 1, 2.5]);
            }
        }
    }

    // WAN port
    color([0.5,0.2,0.2])
    translate([25, -22.5, -5]) {
        difference() {
            cube([10, 5, 5]);
            translate([2,2,2.5])
                cube([6, 1, 2.5]);
        }
    }

    // Power connector
    color([0.8,0.8,0.8])
    translate([32, 22.5, -5]) {
        difference() {
            cube([8, 5, 4]);
            translate([1,1,2])
                cube([6, 3, 2]);
        }
    }

    // Reset button
    color([0.6,0.6,0.6])
    translate([-30, 22.5, -5]) {
        cylinder(h=2, r=2);
    }

    // Rubber feet
    for(pos=[[-30,20,0], [30,20,0], [-30,-20,0], [30,-20,0]]) {
        color([0.05,0.05,0.05])
        translate(pos)
            cylinder(h=1, r=3);
    }
}

module rounded_box(size, radius) {
    x = size[0]/2 - radius;
    y = size[1]/2 - radius;
    z = size[2]/2;

    hull() {
        // Corner spheres
        for(i=[-1,1], j=[-1,1], k=[-1,1]) {
            translate([i*x, j*y, k*z])
                sphere(radius);
        }
    }
}

