module tower() {
    // Central pole
    color("darkgray")
    translate([0, 0, base_h + tower_h/2])
        cylinder(h = tower_h, r = tower_r, $fn = 32);

    // Antenna arrays (4 sides)
    for (angle = [0, 90, 180, 270]) {
        rotate([0, 0, angle])
            translate([tower_r + panel_w/2 + 1, 0, base_h + tower_h * 0.7])
                antenna_panel();
    }

