module antenna_panel() {
    color("white")
    difference() {
        // Main panel
        cube([panel_w, panel_th, panel_h], center = true);

        // Radiating elements
        for (row = [-1, 0, 1])
            for (col = [-1, 0, 1])
                translate([col * 5, 0, row * 4])
                    cube([2, panel_th + 0.1, 1], center = true);
    }
}

module rounded_cube(size, radius) {
    // Create rounded cube using hull of spheres
    hull() {
        for (dx = [-1, 1])
            for (dy = [-1, 1])
                for (dz = [-1, 1])
                    translate([dx * (size[0]/2 - radius), 
