/* ---- helper modules ---- */
module rounded_box(s, r) {
    hull() {
        translate([ s[0]/2-r,  s[1]/2-r,  s[2]/2-r]) sphere(r=r);
        translate([-s[0]/2+r,  s[1]/2-r,  s[2]/2-r]) sphere(r=r);
        translate([ s[0]/2-r, -s[1]/2+r,  s[2]/2-r]) sphere(r=r);
        translate([-s[0]/2+r, -s[1]/2+r,  s[2]/2-r]) sphere(r=r);
        translate([ s[0]/2-r,  s[1]/2-r, -s[2]/2+r]) sphere(r=r);
        translate([-s[0]/2+r,  s[1]/2-r, -s[2]/2+r]) sphere(r=r);
        translate([ s[0]/2-r, -s[1]/2+r, -s[2]/2+r]) sphere(r=r);
        translate([-s[0]/2+r, -s[1]/2+r, -s[2]/2+r]) sphere(r=r);
    }
}

module offset_box(s, r) {
    rounded_box([s[0]-2*r, s[1]-2*r, s[2]], r);
}

