// Rounded cube helper
module rounded_cube(size, radius) {
    hull() {
        translate([radius, radius, 0])
            cylinder(h=size[2], r=radius);
        translate([size[0]-radius, radius, 0])
            cylinder(h=size[2], r=radius);
        translate([size[0]-radius, size[1]-radius, 0])
            cylinder(h=size[2], r=radius);
        translate([radius, size[1]-radius, 0])
            cylinder(h=size[2], r=radius);
        for(x=[radius, size[0]-radius])
            for(y=[radius, size[1]-radius])
                translate([x, y, radius])
                    sphere(r=radius);
    }
}

// Display screen
module display() {
    translate([5, 5, 25.1]) {
        // Screen bezel
        color(color_screen)
            cube([50, 35, 1]);

        // Active display area
        translate([2.5, 2.5, 0.6])
            color(color_display)
                cube([45, 30, 0.5]);
    }
}

// Keypad
module keypad() {
    translate([60, 8, 25.1]) {
        // Keypad base
        color(color_case)
            cube([15, 44, 1]);

        // Number keys 0-9
        for(i=[0:9]) {
            x_pos = (i % 3) * 4.5 + 1.5;
            y_pos = floor(i / 3) * 4.5 + 1.5;
            if(i == 9) {
                x_pos = 4.5;
                y_pos = 4.5 * 3 + 1.5;
            }
            translate([x_pos, y_pos, 1.1])
                color(color_keys)
                    cylinder(h=0.8, r=1.5);
        }

        // Enter key
        translate([1.5, 1, 1.1])
            color(color_keys)
                cube([12, 3, 0.8]);
    }
}

